12     class RetrieveDrawings
13     {
14         public static string result;
15         public static void CopyDrawings(XmlDocument xmlResult, string url)
16         {
17             string key;
18             string payload;
19             
20             string logEvent;
21             string xmlPayload;
22             string sURL = url;
23 
24             StringBuilder data = new StringBuilder();
25 
26             //Timing vars
27             DateTime startTime = DateTime.Now;
28             DateTime endTime = DateTime.Now;
29             TimeSpan ts = endTime.Subtract(startTime);
30             decimal elapsedTimeMS = ts.Milliseconds;
31             decimal elapsedTimeSeconds = ts.Seconds;
32             DateTime totalTimeStart = DateTime.Now;
33             DateTime totalTimeStop = DateTime.Now;
34 
35             try
36             {
37                 logEvent = "Retrieving/Saving Document Files For Order: " + Triggers.pubOrderNumber + " -> " + C1WebService.SPOrderNumber;
38                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
39 
40                 //Retrieve documents for order, reusing xmlResult above
41                 string documentType = "Design";
42                 string documentName = "";
43                 string orderNumber = Triggers.pubOrderNumber;
44                 string[] docs;
45                 docs = new string[50];
46                 int arrayindex = 0;
47 
48                 //Retrieve the serialNumber from the XML 
49                 string configSerial = "";
50                 XmlNodeList xnlSN = xmlResult.GetElementsByTagName("serialNumber");
51                 foreach (XmlNode node in xnlSN)
52                 {
53                     configSerial = node.InnerText;
54                 }
55 
56                 string useMethod = "";
57                 key = "getDocument";
58                 useMethod = C1Dictionaries.webmethods[key];
59                 string documentSerialNumber = configSerial;
60                 arrayindex = 0;
61 
62                 XmlNodeList xnl = xmlResult.GetElementsByTagName("fileName");
63                 foreach (XmlNode node in xnl)
64                 {
65                     docs[arrayindex] = node.InnerText;
66                     arrayindex += 1;
67                 }
68 
69                 logEvent = "DEBUG: Finished building document filename array";
70                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
71 
72                 //We now have an array of document filenames; process by retrieving and saving each file
73                 int upperBound = arrayindex;
74                 arrayindex = 0;
75                 string documentFilesSaved = "";
76                 documentSerialNumber = configSerial;
77                 string SharepointLocation = DatabaseFactory.splocation;
78                 NetworkShare.DisconnectFromShare(SharepointLocation, true);
79                 NetworkShare.ConnectToShare(SharepointLocation, DatabaseFactory.spuname, DatabaseFactory.sppassword);
80 
81                 logEvent = "DEBUG: SP Share credential fixed";
82                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
83 
84                 //Create Sharepoint folder for order#
85                 string SharepointCopyLocation = SharepointLocation + C1WebService.SPOrderNumber + "\\";
86                 try { DirectoryInfo di = Directory.CreateDirectory(SharepointCopyLocation); }
87                 catch (Exception edi)
88                 {
89                     //directory already exists, nothing further needs done
90                 }
91                 
92                 HttpWebRequest objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
93                 objRequest.Method = "POST";
94                 objRequest.ContentType = "text/xml";
95                 objRequest.Headers.Add("SOAPAction", key);
96                 objRequest.Timeout = 120000;        //increased
97                 objRequest.ReadWriteTimeout = 120000;   //increased
98 
99                 while (arrayindex < upperBound)
100                 {
101                     documentName = docs[arrayindex];
102                     xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soapenv:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:ws=" + (char)34 + "http://ws.configureone.com" + (char)34 + " xmlns:mod=" + (char)34 + "http://model.ws.configureone.com" + (char)34 + " xmlns:soapenv=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soapenv:Body><ws:" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><ws:document><mod:type>" + documentType + "</mod:type><mod:serialNumber>" + documentSerialNumber + "</mod:serialNumber><mod:files><mod:fileName>" + documentName + "</mod:fileName><mod:content>" + documentName + "</mod:content></mod:files></ws:document></ws:" + key + "></soapenv:Body></soapenv:Envelope>";
103                     sURL = useMethod;
104                     objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
105                     objRequest.Method = "POST";
106                     objRequest.ContentType = "text/xml";
107                     objRequest.Headers.Add("SOAPAction", key);
108                     objRequest.Timeout = 120000;
109                     objRequest.ReadWriteTimeout = 120000;
110 
111                     logEvent = "DEBUG: Requested document from C1";
112                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
113 
114                     startTime = DateTime.Now;
115                     data = new StringBuilder();
116 
117                     data = new StringBuilder();
118                     data.Append(xmlPayload);
119                     byte[] byteDataDoc = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
120                     objRequest.ContentLength = byteDataDoc.Length;
121 
122                     using (Stream postStream = objRequest.GetRequestStream())
123                     {
124                         postStream.ReadTimeout = 120000;
125                         postStream.WriteTimeout = 120000;
126                         postStream.Write(byteDataDoc, 0, byteDataDoc.Length);
127                     }
128                     xmlResult = new XmlDocument();
129                     result = "";
130 
131                     logEvent = "DEBUG: Waiting for response from C1";
132                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
133 
134                     try
135                     {
136                         using (HttpWebResponse response = (HttpWebResponse)objRequest.GetResponse())
137                         {
138                             StreamReader reader = new StreamReader(response.GetResponseStream());
139                             result = reader.ReadToEnd();
140                             reader.Close();
141                             response.Close();
142 
143                             try
144                             {
145                                 xmlResult.LoadXml(result);
146                             }
147                             catch (Exception ex1)
148                             {
149                                 return;
150                             }
151 
152                             logEvent = "DEBUG: Response received from C1";
153                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
154 
155                             // *** LOG TIME
156                             endTime = DateTime.Now;
157                             ts = endTime.Subtract(startTime);
158                             elapsedTimeMS = ts.Milliseconds;
159                             elapsedTimeSeconds = ts.Seconds;
160                             logEvent = "DEBUG: Document response was received from ConfigureOne in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
161                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
162 
163                             XmlNodeList xnldoc = xmlResult.GetElementsByTagName("content");
164                             foreach (XmlNode node in xnldoc)
165                             {
166                                 logEvent = "DEBUG: Copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex];
167                                 if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
168 
169                                 startTime = DateTime.Now;
170 
171                                 try
172                                 {
173                                     byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
174                                     File.WriteAllBytes(SharepointCopyLocation + C1WebService.SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
175                                     documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
176 
177                                     // *** LOG TIME
178                                     endTime = DateTime.Now;
179                                     ts = endTime.Subtract(startTime);
180                                     elapsedTimeMS = ts.Milliseconds;
181                                     elapsedTimeSeconds = ts.Seconds;
182                                     logEvent = "DEBUG: Document copied to Sharepoint in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
183                                     if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
184                                 }
185                                 catch (Exception ts1)
186                                 {
187                                     logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS1 Exception.  A maximum of 5 retries will be attempted.";
188                                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
189                                     bool successfulDocument = false;
190                                     for (int i = 0; i < 5; i += 1)
191                                     {
192                                         if (successfulDocument) { break; }
193                                         try
194                                         {
195                                             byte[] pdfByteArray = Convert.FromBase64String(node.InnerText);
196                                             File.WriteAllBytes(SharepointCopyLocation + C1WebService.SPOrderNumber + "_" + docs[arrayindex], pdfByteArray);
197                                             documentFilesSaved = documentFilesSaved + docs[arrayindex] + Environment.NewLine;
198 
199                                             // *** LOG TIME
200                                             endTime = DateTime.Now;
201                                             ts = endTime.Subtract(startTime);
202                                             elapsedTimeMS = ts.Milliseconds;
203                                             elapsedTimeSeconds = ts.Seconds;
204                                             logEvent = "DEBUG: Document copied to Sharepoint in: " + Convert.ToString(elapsedTimeMS) + "ms / " + Convert.ToString(elapsedTimeSeconds) + " s";
205                                             if (DatabaseFactory.debugLogging) { System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234); }
206                                             successfulDocument = true;
207                                         }
208                                         catch (Exception rt1)
209                                         {
210                                             logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "rt1 Exception on Retry#: " + (i + 1).ToString();
211                                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
212                                         }
213                                     }
214 
215                                     if (!successfulDocument)
216                                     {
217                                         // *** LOG TIME
218                                         endTime = DateTime.Now;
219                                         ts = endTime.Subtract(startTime);
220                                         elapsedTimeMS = ts.Milliseconds;
221                                         elapsedTimeSeconds = ts.Seconds;
222                                         logEvent = "DEBUG: Time that elapsed before timeout occurred for current document: " + Convert.ToString(elapsedTimeMS) + "ms, " + Convert.ToString(elapsedTimeSeconds) + " s";
223                                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
224                                         SendMail.MailMessage(logEvent, "TIMEOUT");
225                                     }
226 
227                                 }
228                             }
229                         }
230                     }
231                     catch (Exception ts2)
232                     {
233                         logEvent = "An error occurred copying file to Sharepoint: " + C1WebService.SPOrderNumber + "_" + docs[arrayindex] + "  >  " + "TS2 Exception";
234                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
235                         SendMail.MailMessage(logEvent, "TIMEOUT");
236                     }
237                     arrayindex += 1;
238                 }
239                 
240                 //Save XML output to SharePoint as well
241                 File.Copy("XMLOutput_" + C1WebService.SPPUBOrderNumber + ".txt", SharepointCopyLocation + "XMLOutput_" + C1WebService.SPPUBOrderNumber + ".txt", true);
242 
243                 logEvent = "Retrieved/Saved Following Document Files: " + Environment.NewLine + Environment.NewLine + documentFilesSaved;
244                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
245 
246                 //Update order-status and ref number in C1 to Ordered and SL order#, respectively
247                 key = "updateOrder";
248                 useMethod = C1Dictionaries.webmethods[key];
249                 string C1orderNumber = orderNumber;
250                 string C1orderReference = C1WebService.SPOrderNumber;
251                 string C1orderStatus = "OR";              //Ordered
252                 xmlPayload = "<?xml version=\"1.0\" encoding=\"utf-8\"?><soap:Envelope xmlns:xsi=" + (char)34 + "http://www.w3.org/2001/XMLSchema-instance" + (char)34 + " xmlns:xsd=" + (char)34 + "http://www.w3.org/2001/XMLSchema" + (char)34 + " xmlns:soap=" + (char)34 + "http://schemas.xmlsoap.org/soap/envelope/" + (char)34 + ">" + "<soap:Body><" + key + " xmlns=" + (char)34 + "http://ws.configureone.com" + (char)34 + "><param1>" + orderNumber + "</param1><param2>" + C1orderReference + "</param2><param3>" + C1orderStatus + "</param3></" + key + "></soap:Body></soap:Envelope>";
253                 sURL = useMethod;
254                 C1WebService.pubURL = sURL;
255                 C1WebService.pubPayload = xmlPayload;
256                 logEvent = "XML Payload To AXIS: " + xmlPayload;
257                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
258                 objRequest = (HttpWebRequest)WebRequest.Create(sURL.ToString());
259                 objRequest.Method = "POST";
260                 objRequest.ContentType = "text/xml";
261                 objRequest.Headers.Add("SOAPAction", key);
262                 objRequest.Timeout = 120000;
263                 objRequest.ReadWriteTimeout = 120000;
264 
265                 data = new StringBuilder();
266                 data.Append(xmlPayload);
267                 byte[] byteDataStatus = Encoding.UTF8.GetBytes(data.ToString());          // Sending our request to Apache AXIS in a byte array
268                 objRequest.ContentLength = byteDataStatus.Length;
269                 using (Stream postStream = objRequest.GetRequestStream())
270                 {
271                     postStream.ReadTimeout = 120000;
272                     postStream.WriteTimeout = 120000;
273                     postStream.Write(byteDataStatus, 0, byteDataStatus.Length);
274                 }
275 
276                 startTime = DateTime.Now;
277                 
278                 //return response from AXIS (if any)
279                 using (HttpWebResponse response = objRequest.GetResponse() as HttpWebResponse)
280                 {
281                     StreamReader reader = new StreamReader(response.GetResponseStream());
282                     result = reader.ReadToEnd();
283                     reader.Close();
284                     response.Close();
285                 }
286 
287                 try
288                 {
289                     xmlResult.LoadXml(result);
290                     xnl = xmlResult.GetElementsByTagName("success");
291                     foreach (XmlNode node in xnl)
292                     {
293                         switch (node.InnerText)
294                         {
295                             case "true":
296                                 logEvent = "Status-Set Result: Success";
297                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
298                                 break;
299                             default:
300                                 logEvent = "Status-Set Result: Failure";
301                                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
302                                 break;
303                         }
304                     }
305                 }
306                 catch (Exception ex2)
307                 {
308                     logEvent = "ERROR LOADING XML FROM WEB SERVICE ON C1 ORDER STATUS SET: " + ex2.Message;
309                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
310                     return;
311                 }
312 
313                 logEvent = "C1 Order Status And Reference Completed For C1 Order: " + orderNumber + " - Syteline Order: " + C1WebService.SPOrderNumber;
314                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
315             }
316             catch (Exception exd)
317             {
318                 logEvent = "ERROR: " + exd.Message + " -> " + exd.Source;
319                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
320             }
321         }
322     }
