41                 switch (foundSite == true)
42                 {
43                     case true:
44                         string rplConnectionString = DatabaseFactory.connectionString;
45                         int csPos = rplConnectionString.IndexOf("NOVB");
46                         DatabaseFactory.connectionString = rplConnectionString.Substring(0, csPos) + dbSite + rplConnectionString.Substring(csPos + 4, rplConnectionString.Length - (csPos + 4));
47                         Triggers.logEvent = "Connection String: " + DatabaseFactory.connectionString;
48                         //System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
