459                 foreach (XmlNode nodeisv in xnlisv)
460                 {
461                     if (nodeisv.ChildNodes[2].InnerText == "SHIP_VIA") { co.ShipVia = nodeisv.ChildNodes[0].Attributes["name"].Value; }
462                 }
463                 
464                 //item-master for the line we are working with
465                 int recordSequence = 1;
466                 XmlNodeList xnlim = detailDoc.GetElementsByTagName("ItemMaster");
467                 citem.CO_Line = coitem.CO_Line;
468                 string cost = "";
469                 string price = "";
470                 string sell = "";
471                 string weight = "";
472                 citem.IM_VAR1 = "";
473                 citem.IM_VAR2 = "";
474                 citem.IM_VAR3 = "";
475                 citem.IM_VAR4 = "";
476                 citem.IM_VAR5 = "";
477                 //Build nodelist of ItemMaster nodes and a second nodelist within of its childnodes so we can reference those elements by name instead of index
478                 foreach (XmlNode nodeim in xnlim)
479                 {
480                     foreach (XmlNode childIM in nodeim.ChildNodes)
481                     {
482                         switch (childIM.Name)
483                         {
484                             case "SMARTPART_NUM":
485                                 citem.Smartpart = childIM.InnerText;
486                                 break;
487                             case "ITEM_NUM":
488                                 citem.Item = childIM.InnerText;
489                                 break;
490                             case "DESCRIPTION":
491                                 citem.Desc = childIM.InnerText;
492                                 break;
493                             case "COST":
494                                 cost = childIM.InnerText;
495                                 break;
496                             case "PRICE":
497                                 price = childIM.InnerText;
498                                 break;
499                             case "SELL_PRICE":
500                                 sell = childIM.InnerText;
501                                 break;
502                             case "WEIGHT":
503                                 weight = childIM.InnerText;
504                                 break;
505                             case "UOM":
506                                 citem.UnitOfMeasure = childIM.InnerText;
507                                 break;
508                             case "PRIORITY_LEVEL":
509                                 citem.PriorityLevel = Convert.ToInt16(childIM.InnerText);
510                                 break;
511                             case "VAR_1":
512                                 citem.IM_VAR1 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
513                                 break;
514                             case "VAR_2":
515                                 citem.IM_VAR2 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
516                                 break;
517                             case "VAR_3":
518                                 citem.IM_VAR3 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
519                                 break;
520                             case "VAR_4":
521                                 citem.IM_VAR4 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
522                                 break;
523                             case "VAR_5":
524                                 citem.IM_VAR5 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
525                                 break;
526                             default:
527                                 //do nothing
528                                 break;
529                         }
530                         citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
531                         citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
532                         citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
533                         citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
534                         citem.Sequence = recordSequence;
535                     }
536                     recordSequence += 1;
537                     DatabaseFactory.WriteRecordCItem(ref citem);
538                     cost = "";
539                     price = "";
540                     sell = "";
541                     weight = "";
542                     citem.IM_VAR1 = "";
543                     citem.IM_VAR2 = "";
544                     citem.IM_VAR3 = "";
545                     citem.IM_VAR4 = "";
546                     citem.IM_VAR5 = "";
547                 }
548 
549                 //BOM records (Must load in this manner, as none of these have static variable names or attributes)
550                 recordSequence = 0;
551                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
552                 bom.CO_Line = coitem.CO_Line;
553                 foreach (XmlNode nodeib in xnlb)
554                 {
555                     recordSequence += 1;
556                     bom.Sequence = recordSequence;
557                     bom.RecordSequence = recordSequence;
558                     var parent = nodeib.SelectSingleNode("..");
559                     string parentID = parent.ChildNodes[0].InnerText;
560                     bom.Parent = parentID == "1" ? null : parentID;
561                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
562                     bom.Item = nodeib.ChildNodes[1].InnerText;
563                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
564                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
565                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
566                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
567                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
568                     
569                     //Search the ItemMaster XML records for the matching smartpart_num and retrieve its priority level for this BOM record
570                     bool foundchild = false;
571                     bool imdone = false;
572                     foreach (XmlNode nodeIMPL in xnlim)
573                     {
574                         if (imdone == true)
575                         {
576                             break;
577                         }
578                         foreach (XmlNode childIMPL in nodeIMPL.ChildNodes)
579                         {
580                             if (imdone == true)
581                             {
582                                 break;
583                             }
584                             if (childIMPL.InnerText == bom.Smartpart)
585                             {
586                                 foundchild = true;
587                             }
588                             if (foundchild == true)
589                             {
590                                 if (childIMPL.Name == "PRIORITY_LEVEL")
591                                 {
592                                     bom.PriorityLevel = Convert.ToInt16(childIMPL.InnerText);
593                                     imdone = true;
594                                 }
595                             }
596                         }
597                     }
598                     //output BOM record
599                     DatabaseFactory.WriteRecordBOM(ref bom);
600                 }
601                 
602                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
603                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
604                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
605             }
606             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
607         }
608     }
609 }
