9     class StagingUtilities
10     {
11         public static string globalOrderNum;
12         public static int globalOrderLineNum;
13                 
14         public static void MapXMLToSQL(XmlDocument xmldoc)
15         {
16             zCfgCO co = new zCfgCO();
17             zCfgCOitem coitem = new zCfgCOitem();
18             zCfgItem citem = new zCfgItem();
19             zCfgParmVal cfg = new zCfgParmVal();
20             zCfgBOM bom = new zCfgBOM();
21 
22             Audit.resetmE = true;       //reset the mE array in case we have any mapping errors to report for this cycle
23             
24             //*** Determine what site we are working with and re-set the connection string accordingly, else default to NOVB
25             XmlNodeList xnlsite = xmldoc.GetElementsByTagName("Input");
26             foreach (XmlNode node in xnlsite)
27             {
28                 switch (node.ChildNodes[2].InnerText == "ORDER SITE")
29                 {
30                     case true:
31                         string rplConnectionString = DatabaseFactory.connectionString;
32                         int csPos = rplConnectionString.IndexOf("NOVB");
33                         DatabaseFactory.connectionString = rplConnectionString.Substring(0, csPos) + node.ChildNodes[0].Attributes["name"].Value + rplConnectionString.Substring(csPos + 4, rplConnectionString.Length - (csPos + 4));
34                         Triggers.logEvent = "Connection String: " + DatabaseFactory.connectionString;
35                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
36                         break;
37                     default:
38                         //continue with our NOVB default
39                         break;
40                 }
41             }
42 
43             //Retrieve due date
44             XmlNodeList xnlduedate = xmldoc.GetElementsByTagName("Input");
45             foreach (XmlNode node in xnlduedate)
46             {
47                 switch (node.ChildNodes[2].InnerText == "DUE DATE")
48                 {
49                     case true:
50                         co.DueDate = Convert.ToDateTime(node.ChildNodes[0].Attributes["name"].Value);
51                         coitem.DueDate = Convert.ToDateTime(node.ChildNodes[0].Attributes["name"].Value);
52                         break;
53                     default:
54                         co.DueDate = DateTime.Now;
55                         coitem.DueDate = DateTime.Now;
56                         break;
57                 }
58             }
59             
60             //Pre-staging activities
61             XmlNodeList xnl = xmldoc.GetElementsByTagName("ORDER_NUM");
62             foreach (XmlNode node in xnl)
63             {
64                 co.CO_Num = node.InnerText;
65                 coitem.CO_Num = node.InnerText;
66                 cfg.CO_Num = node.InnerText;
67                 citem.CO_Num = node.InnerText;
68                 bom.CO_Num = node.InnerText;
69                 globalOrderNum = co.CO_Num;
70                 globalOrderLineNum = 0;
71             }
72             
73             switch (co.CO_Num == null || co.CO_Num == "")
74             {
75                 case true:
76                     Triggers.logEvent = "ORDER NUMBER NOT FOUND: " + co.CO_Num;
77                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
78                     return;
79                 default:
80                     //Remove any pre-existing records in SQL for this order
81                     DatabaseFactory.CleanupOrder(co.CO_Num);
82                     break;
83             }
84             Triggers.logEvent = "MAPPING XML TO STAGING TABLES";
85             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
86 
87             //build CO header
88             xnl = xmldoc.GetElementsByTagName("ID");
89             foreach (XmlNode node in xnl)
90             {
91                 co.Identifier = node.InnerText;
92                 break;          //we want first occurrence, ONLY
93             }
94             xnl = xmldoc.GetElementsByTagName("ORDER_REF_NUM");
95             foreach (XmlNode node in xnl)
96             {
97                 co.CORefNum = node.InnerText;
98             }
99             xnl = xmldoc.GetElementsByTagName("CUST_NAME");
100             foreach (XmlNode node in xnl)
101             {
102                 co.CustName = node.InnerText;
103             }
104             xnl = xmldoc.GetElementsByTagName("CUST_REF_NUM");
105             foreach (XmlNode node in xnl)
106             {
107                 co.CustRefNum = node.InnerText;
108             }
109             xnl = xmldoc.GetElementsByTagName("ACCOUNT_NUM");
110             foreach (XmlNode node in xnl)
111             {
112                 co.AccountNum = node.InnerText;
113             }
114             xnl = xmldoc.GetElementsByTagName("ERP_REFERENCE_NUM");
115             foreach (XmlNode node in xnl)
116             {
117                 co.ErpReferenceNum = node.InnerText;
118             }
119             xnl = xmldoc.GetElementsByTagName("PROJECT");
120             foreach (XmlNode node in xnl)
121             {
122                 co.Project = node.InnerText;
123             }
124             xnl = xmldoc.GetElementsByTagName("PAYMENT_TERMS");
125             foreach (XmlNode node in xnl)
126             {
127                 co.PaymentTerms = node.InnerText;
128             }
129             xnl = xmldoc.GetElementsByTagName("SHIP_VIA");
130             foreach (XmlNode node in xnl)
131             {
132                 co.ShipVia = node.InnerText;
133             }
134             xnl = xmldoc.GetElementsByTagName("SHIPPING_TERMS");
135             foreach (XmlNode node in xnl)
136             {
137                 co.ShippingTerms = node.InnerText;
138             }
139             xnl = xmldoc.GetElementsByTagName("BILL_TO_CONTACT_NAME");
140             foreach (XmlNode node in xnl)
141             {
142                 co.BillToContactName = node.InnerText;
143             }
144             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_1");
145             foreach (XmlNode node in xnl)
146             {
147                 co.BillToAddressLine1 = node.InnerText;
148             }
149             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_2");
150             foreach (XmlNode node in xnl)
151             {
152                 co.BillToAddressLine2 = node.InnerText;
153             }
154             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_3");
155             foreach (XmlNode node in xnl)
156             {
157                 co.BillToAddressLine3 = node.InnerText;
158             }
159             xnl = xmldoc.GetElementsByTagName("BILL_TO_CITY");
160             foreach (XmlNode node in xnl)
161             {
162                 co.BillToCity = node.InnerText;
163             }
164             xnl = xmldoc.GetElementsByTagName("BILL_TO_STATE");
165             foreach (XmlNode node in xnl)
166             {
167                 co.BillToState = node.InnerText;
168             }
169             xnl = xmldoc.GetElementsByTagName("BILL_TO_COUNTRY");
170             foreach (XmlNode node in xnl)
171             {
172                 co.BillToCountry = node.InnerText;
173             }
174             xnl = xmldoc.GetElementsByTagName("BILL_TO_POSTAL_CODE");
175             foreach (XmlNode node in xnl)
176             {
177                 co.BillToPostalCode = node.InnerText;
178             }
179             xnl = xmldoc.GetElementsByTagName("BILL_TO_PHONE_NUMBER");
180             foreach (XmlNode node in xnl)
181             {
182                 co.BillToPhoneNumber = node.InnerText;
183                 if (co.BillToPhoneNumber.Length == 0) { co.BillToPhoneNumber = "NA"; };
184             }
185             xnl = xmldoc.GetElementsByTagName("BILL_TO_FAX_NUMBER");
186             foreach (XmlNode node in xnl)
187             {
188                 co.BillToFaxNumber = node.InnerText;
189             }
190             xnl = xmldoc.GetElementsByTagName("BILL_TO_EMAIL_ADDRESS");
191             foreach (XmlNode node in xnl)
192             {
193                 co.BillToEmailAddress = node.InnerText;
194             }
195             xnl = xmldoc.GetElementsByTagName("BILL_TO_REF_NUM");
196             foreach (XmlNode node in xnl)
197             {
198                 co.BillToRefNum = node.InnerText;
199             }
200             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CONTACT_NAME");
201             foreach (XmlNode node in xnl)
202             {
203                 co.ShipToContactName = node.InnerText;
204             }
205             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_1");
206             foreach (XmlNode node in xnl)
207             {
208                 co.ShipToAddressLine1 = node.InnerText;
209             }
210             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_2");
211             foreach (XmlNode node in xnl)
212             {
213                 co.ShipToAddressLine2 = node.InnerText;
214             }
215             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_3");
216             foreach (XmlNode node in xnl)
217             {
218                 co.ShipToAddressLine3 = node.InnerText;
219             }
220             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CITY");
221             foreach (XmlNode node in xnl)
222             {
223                 co.ShipToCity = node.InnerText;
224             }
225             xnl = xmldoc.GetElementsByTagName("SHIP_TO_STATE");
226             foreach (XmlNode node in xnl)
227             {
228                 co.ShipToState = node.InnerText;
229             }
230             xnl = xmldoc.GetElementsByTagName("SHIP_TO_COUNTRY");
231             foreach (XmlNode node in xnl)
232             {
233                 co.ShipToCountry = node.InnerText;
234             }
235             xnl = xmldoc.GetElementsByTagName("SHIP_TO_POSTAL_CODE");
236             foreach (XmlNode node in xnl)
237             {
238                 co.ShipToPostalCode = node.InnerText;
239             }
240             xnl = xmldoc.GetElementsByTagName("SHIP_TO_PHONE_NUMBER");
241             foreach (XmlNode node in xnl)
242             {
243                 co.ShipToPhoneNumber = node.InnerText;
244             }
245             xnl = xmldoc.GetElementsByTagName("SHIP_TO_FAX_NUMBER");
246             foreach (XmlNode node in xnl)
247             {
248                 co.ShipToFaxNumber = node.InnerText;
249             }
250             xnl = xmldoc.GetElementsByTagName("SHIP_TO_EMAIL_ADDRESS");
251             foreach (XmlNode node in xnl)
252             {
253                 co.ShipToEmailAddress = node.InnerText;
254             }
255             xnl = xmldoc.GetElementsByTagName("SHIP_TO_REF_NUM");
256             foreach (XmlNode node in xnl)
257             {
258                 co.ShipToRefNum = node.InnerText;
259             }
260             xnl = xmldoc.GetElementsByTagName("PRIORITY_LEVEL");
261             foreach (XmlNode node in xnl)
262             {
263                 co.PriorityLevel = Convert.ToInt16(node.InnerText);
264             }
265             xnl = xmldoc.GetElementsByTagName("SERIAL_NUMBER");
266             foreach (XmlNode node in xnl)
267             {
268                 co.QuoteNbr = node.InnerText;
269                 if (co.QuoteNbr.Length == 0) { co.QuoteNbr = "N/A"; };
270             }
271             xnl = xmldoc.GetElementsByTagName("CREATED_BY_USER_ID");
272             foreach (XmlNode node in xnl)
273             {
274                 co.WebUserName = DatabaseFactory.UserName(node.InnerText);
275             }
276 
277             co.WebOrderDate = System.DateTime.Now;
278             
279             //Look for PURCHASE ORDER in INPUTS, load into CO and COITEM
280             XmlNodeList xnlPO = xmldoc.GetElementsByTagName("Input");
281             foreach (XmlNode nodePO in xnlPO)
282             {
283                 if (nodePO.ChildNodes[2].InnerText == "PURCHASE ORDER")
284                 {
285                     co.CustPO = nodePO.ChildNodes[0].Attributes["name"].Value;
286                     coitem.CustPO = nodePO.ChildNodes[0].Attributes["name"].Value;
287                 }
288             }
289 
290             //Look for FREIGHT ACCOUNT# (IF COLLECT) in INPUTS, load into CO
291             XmlNodeList xnlCO = xmldoc.GetElementsByTagName("Input");
292             foreach (XmlNode nodeCO in xnlCO)
293             {
294                 if (nodeCO.ChildNodes[2].InnerText == "FREIGHT ACCOUNT# (IF COLLECT)")
295                 {
296                     switch(nodeCO.ChildNodes[0].Attributes["name"].Value.Length > 0)
297                     {
298                         case true:
299                             co.FreightAcct = nodeCO.ChildNodes[0].Attributes["name"].Value;
300                             break;
301                         default:
302                             co.FreightAcct = "N/A";
303                             break;
304                     }
305                 }
306             }
307 
308             //Look for FREIGHT TERMS in INPUTS, load into CO
309             XmlNodeList xnlCOFT = xmldoc.GetElementsByTagName("Input");
310             foreach (XmlNode nodeCOFT in xnlCOFT)
311             {
312                 if (nodeCOFT.ChildNodes[2].InnerText == "FREIGHT TERMS")
313                 {
314                     switch (nodeCOFT.ChildNodes[0].Attributes["name"].Value.Length > 0)
315                     {
316                         case true:
317                             co.FreightTerms = nodeCOFT.ChildNodes[0].Attributes["name"].Value;
318                             break;
319                         default:
320                             co.FreightTerms = "N/A";
321                             break;
322                     }
323                 }
324             }
325             
326             //build COITEM records, per line
327             xnl = xmldoc.GetElementsByTagName("Detail");
328             foreach (XmlNode node in xnl)
329             {
330                 coitem.CO_Line = Convert.ToInt16(node.ChildNodes[0].InnerText);
331                 coitem.Serial = node.ChildNodes[2].InnerText;
332                 coitem.Item = node.ChildNodes[3].InnerText;
333                 coitem.Smartpart = node.ChildNodes[4].InnerText;
334                 coitem.Desc = node.ChildNodes[5].InnerText;
335                 coitem.ConfigType = node.ChildNodes[6].InnerText;
336                 coitem.UnitPrice = Convert.ToDecimal(node.ChildNodes[7].InnerText);
337                 coitem.UnitCost = Convert.ToDecimal(node.ChildNodes[8].InnerText);
338                 coitem.Discount = Convert.ToDecimal(node.ChildNodes[9].InnerText);
339                 coitem.QTY = Convert.ToDecimal(node.ChildNodes[10].InnerText);
340                 coitem.PriorityLevel = co.PriorityLevel;
341                 globalOrderLineNum = coitem.CO_Line;
342                 //output coitem record
343                 DatabaseFactory.WriteRecordCOItem(ref coitem);
344                 
345                 //*** Everything else here builds on the COITEM ***
346                 var detailParent = node.SelectSingleNode(".");     //ensure we traverse ONLY children of this node (Detail) as the new parent (root) element
347                 XmlDocument detailDoc = new XmlDocument();
348                 detailDoc.LoadXml(detailParent.OuterXml);
349 
350                 //iterate through Inputs for the line
351                 XmlNodeList xnli = detailDoc.GetElementsByTagName("Input");
352                 foreach (XmlNode nodei in xnli)
353                 {
354                     cfg.CO_Line = coitem.CO_Line;
355                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
356                     cfg.CValue = nodei.ChildNodes[0].InnerText;
357                     cfg.CType = nodei.ChildNodes[1].InnerText;
358                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
359                     cfg.PriorityLevel = co.PriorityLevel;
360                     //output cfg (parmval) record
361                     DatabaseFactory.WriteRecordCfg(ref cfg);
362                 }
363 
364                 //Look for SHIP_VIA in INPUTS
365                 XmlNodeList xnlisv = xmldoc.GetElementsByTagName("Input");
366                 foreach (XmlNode nodeisv in xnlisv)
367                 {
368                     if (nodeisv.ChildNodes[2].InnerText == "SHIP_VIA") { co.ShipVia = nodeisv.ChildNodes[0].Attributes["name"].Value; }
369                 }
370                 
371                 //If dropship has value, override the order-header shipping with this information instead
372                 //UPDATE 6/16/2017:  Preserve the selected ship-to from C1 and if dropship has value, load up the dropship fields
373                 XmlNodeList xnlds = xmldoc.GetElementsByTagName("Input");
374                 foreach (XmlNode nodeds in xnlds)
375                 {
376                     if (nodeds.ChildNodes[2].InnerText.Length >= 9 && nodeds.ChildNodes[2].InnerText.Substring(0, 9) == "DROP SHIP" && nodeds.ChildNodes[0].Attributes["name"].Value != "")
377                     {
378                         switch (nodeds.ChildNodes[2].InnerText)
379                         {
380                             case "DROP SHIP NAME":
381                                 //co.ShipToContactName = nodeds.ChildNodes[0].Attributes["name"].Value;
382                                 co.DropShipContact = nodeds.ChildNodes[0].Attributes["name"].Value;
383                                 break;
384                             case "DROP SHIP ADDRESS 1":
385                                 //co.ShipToAddressLine1 = nodeds.ChildNodes[0].Attributes["name"].Value;
386                                 co.DropShipAddress1 = nodeds.ChildNodes[0].Attributes["name"].Value;
387                                 break;
388                             case "DROP SHIP ADDRESS 2":
389                                 //co.ShipToAddressLine2 = nodeds.ChildNodes[0].Attributes["name"].Value;
390                                 co.DropShipAddress2 = nodeds.ChildNodes[0].Attributes["name"].Value;
391                                 break;
392                             case "DROP SHIP ADDRESS 3":
393                                 //co.ShipToAddressLine3 = nodeds.ChildNodes[0].Attributes["name"].Value;
394                                 co.DropShipAddress3 = nodeds.ChildNodes[0].Attributes["name"].Value;
395                                 break;
396                             case "DROP SHIP ADDRESS 4":
397                                 //co.ShipToAddressLine4 = nodeds.ChildNodes[0].Attributes["name"].Value;
398                                 co.DropShipAddress4 = nodeds.ChildNodes[0].Attributes["name"].Value;
399                                 break;
400                             case "DROP SHIP CITY":
401                                 //co.ShipToCity = nodeds.ChildNodes[0].Attributes["name"].Value;
402                                 co.DropShipCity = nodeds.ChildNodes[0].Attributes["name"].Value;
403                                 break;
404                             case "DROP SHIP STATE":
405                                 //co.ShipToState = nodeds.ChildNodes[0].Attributes["name"].Value;
406                                 co.DropShipState = nodeds.ChildNodes[0].Attributes["name"].Value;
407                                 break;
408                             case "DROP SHIP ZIP CODE":
409                                 //co.ShipToPostalCode = nodeds.ChildNodes[0].Attributes["name"].Value;
410                                 co.DropShipZip = nodeds.ChildNodes[0].Attributes["name"].Value;
411                                 break;
412                             case "DROP SHIP CONTACT":
413                                 //co.ShipToContactName = nodeds.ChildNodes[0].Attributes["name"].Value;
414                                 co.DropShipContact = nodeds.ChildNodes[0].Attributes["name"].Value;
415                                 break;
416                             case "DROP SHIP PHONE":
417                                 //co.ShipToPhoneNumber = nodeds.ChildNodes[0].Attributes["name"].Value;
418                                 co.DropShipPhone = nodeds.ChildNodes[0].Attributes["name"].Value;
419                                 break;
420                             case "DROP SHIP COUNTRY":
421                                 //co.ShipToCountry = nodeds.ChildNodes[0].Attributes["name"].Value;
422                                 co.DropShipCountry = nodeds.ChildNodes[0].Attributes["name"].Value;
423                                 break;
424                             case "DROP SHIP EMAIL":
425                                 //co.ShipToEmailAddress = nodeds.ChildNodes[0].Attributes["name"].Value;
426                                 co.DropShipEmail = nodeds.ChildNodes[0].Attributes["name"].Value;
427                                 break;
428                             default:
429                                 //do nothing
430                                 break;
431                         }
432                     }
433                 }
434                 
435                 //item-master for the line we are working with
436                 int recordSequence = 1;
437                 XmlNodeList xnlim = detailDoc.GetElementsByTagName("ItemMaster");
438                 citem.CO_Line = coitem.CO_Line;
439                 foreach (XmlNode nodeim in xnlim)
440                 {
441                     citem.Sequence = recordSequence;
442                     citem.Smartpart = nodeim.ChildNodes[0].InnerText;
443                     citem.Item = nodeim.ChildNodes[1].InnerText;
444                     citem.Desc = nodeim.ChildNodes[3].InnerText;
445                     string cost = nodeim.ChildNodes[4].InnerText;
446                     string price = nodeim.ChildNodes[5].InnerText;
447                     string sell = nodeim.ChildNodes[6].InnerText;
448                     string weight = nodeim.ChildNodes[9].InnerText;
449                     citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
450                     citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
451                     citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
452                     citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
453                     citem.UnitOfMeasure = nodeim.ChildNodes[26].InnerText;
454                     citem.PriorityLevel = Convert.ToInt16(nodeim.ChildNodes[38].InnerText);
455                     recordSequence += 1;
456                     //output item-master record if priority is <= 2
457                     if (citem.PriorityLevel <= 2)
458                     {
459                         DatabaseFactory.WriteRecordCItem(ref citem);
460                     }
461                 }
462                 //BOM records
463                 recordSequence = 0;
464                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
465                 bom.CO_Line = coitem.CO_Line;
466                 foreach (XmlNode nodeib in xnlb)
467                 {
468                     recordSequence = 1;
469                     bom.Sequence = recordSequence;
470                     var parent = nodeib.SelectSingleNode("..");
471                     string parentID = parent.ChildNodes[0].InnerText;
472                     bom.Parent = parentID == "1" ? null : parentID;
473                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
474                     bom.Item = nodeib.ChildNodes[1].InnerText;
475                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
476                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
477                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
478                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
479                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
480                     bom.PriorityLevel = co.PriorityLevel;
481                     //output BOM record
482                     DatabaseFactory.WriteRecordBOM(ref bom);
483                 }
484                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
485                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
486                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
487             }
488             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
489         }
490     }
491 }
