1 using System;
2 using System.Collections.Generic;
3 using System.Linq;
4 using System.Text;
5 //using System.Threading.Tasks;
6 using System.Xml;
7 
8 namespace ConfigureOneFlag
9 {
13     class StagingUtilities
14     {
15         public static string globalOrderNum;
16         public static int globalOrderLineNum;
17                 
18         public static void MapXMLToSQL(XmlDocument xmldoc)
19         {
20             zCfgCO co = new zCfgCO();
21             zCfgCOitem coitem = new zCfgCOitem();
22             zCfgItem citem = new zCfgItem();
23             zCfgParmVal cfg = new zCfgParmVal();
24             zCfgBOM bom = new zCfgBOM();
25 
26             Audit.resetmE = true;       //reset the mE array in case we have any mapping errors to report for this cycle
27 
28             //Load staging-table objects from XML
29             XmlNodeList xnl = xmldoc.GetElementsByTagName("ORDER_NUM");
30             foreach (XmlNode node in xnl)
31             {
32                 co.CO_Num = node.InnerText;
33                 coitem.CO_Num = node.InnerText;
34                 cfg.CO_Num = node.InnerText;
35                 citem.CO_Num = node.InnerText;
36                 bom.CO_Num = node.InnerText;
37                 globalOrderNum = co.CO_Num;
38                 globalOrderLineNum = 0;
39             }
40             
41             switch (co.CO_Num == null || co.CO_Num == "")
42             {
43                 case true:
44                     Triggers.logEvent = "ORDER NUMBER NOT FOUND: " + co.CO_Num;
45                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
46                     return;
47                 default:
48                     //Remove any pre-existing records in SQL for this order
49                     DatabaseFactory.CleanupOrder(co.CO_Num);
50                     break;
51             }
52             Triggers.logEvent = "MAPPING XML TO STAGING TABLES";
53             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
54 
55             //build CO header
56             xnl = xmldoc.GetElementsByTagName("ID");
57             foreach (XmlNode node in xnl)
58             {
59                 co.Identifier = node.InnerText;
60                 break;          //we want first occurrence, ONLY
61             }
62             xnl = xmldoc.GetElementsByTagName("ORDER_REF_NUM");
63             foreach (XmlNode node in xnl)
64             {
65                 co.CORefNum = node.InnerText;
66             }
67             xnl = xmldoc.GetElementsByTagName("CUST_NAME");
68             foreach (XmlNode node in xnl)
69             {
70                 co.CustName = node.InnerText;
71             }
72             xnl = xmldoc.GetElementsByTagName("CUST_REF_NUM");
73             foreach (XmlNode node in xnl)
74             {
75                 co.CustRefNum = node.InnerText;
76             }
77             xnl = xmldoc.GetElementsByTagName("ACCOUNT_NUM");
78             foreach (XmlNode node in xnl)
79             {
80                 co.AccountNum = node.InnerText;
81             }
82             xnl = xmldoc.GetElementsByTagName("ERP_REFERENCE_NUM");
83             foreach (XmlNode node in xnl)
84             {
85                 co.ErpReferenceNum = node.InnerText;
86             }
87             xnl = xmldoc.GetElementsByTagName("PROJECT");
88             foreach (XmlNode node in xnl)
89             {
90                 co.Project = node.InnerText;
91             }
92             xnl = xmldoc.GetElementsByTagName("PAYMENT_TERMS");
93             foreach (XmlNode node in xnl)
94             {
95                 co.PaymentTerms = node.InnerText;
96             }
97             xnl = xmldoc.GetElementsByTagName("SHIP_VIA");
98             foreach (XmlNode node in xnl)
99             {
100                 co.ShipVia = node.InnerText;
101             }
102             xnl = xmldoc.GetElementsByTagName("SHIPPING_TERMS");
103             foreach (XmlNode node in xnl)
104             {
105                 co.ShippingTerms = node.InnerText;
106             }
107             xnl = xmldoc.GetElementsByTagName("BILL_TO_CONTACT_NAME");
108             foreach (XmlNode node in xnl)
109             {
110                 co.BillToContactName = node.InnerText;
111             }
112             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_1");
113             foreach (XmlNode node in xnl)
114             {
115                 co.BillToAddressLine1 = node.InnerText;
116             }
117             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_2");
118             foreach (XmlNode node in xnl)
119             {
120                 co.BillToAddressLine2 = node.InnerText;
121             }
122             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_3");
123             foreach (XmlNode node in xnl)
124             {
125                 co.BillToAddressLine3 = node.InnerText;
126             }
127             xnl = xmldoc.GetElementsByTagName("BILL_TO_CITY");
128             foreach (XmlNode node in xnl)
129             {
130                 co.BillToCity = node.InnerText;
131             }
132             xnl = xmldoc.GetElementsByTagName("BILL_TO_STATE");
133             foreach (XmlNode node in xnl)
134             {
135                 co.BillToState = node.InnerText;
136             }
137             xnl = xmldoc.GetElementsByTagName("BILL_TO_COUNTRY");
138             foreach (XmlNode node in xnl)
139             {
140                 co.BillToCountry = node.InnerText;
141             }
142             xnl = xmldoc.GetElementsByTagName("BILL_TO_POSTAL_CODE");
143             foreach (XmlNode node in xnl)
144             {
145                 co.BillToPostalCode = node.InnerText;
146             }
147             xnl = xmldoc.GetElementsByTagName("BILL_TO_PHONE_NUMBER");
148             foreach (XmlNode node in xnl)
149             {
150                 co.BillToPhoneNumber = node.InnerText;
151                 if (co.BillToPhoneNumber.Length == 0) { co.BillToPhoneNumber = "NA"; };
152             }
153             xnl = xmldoc.GetElementsByTagName("BILL_TO_FAX_NUMBER");
154             foreach (XmlNode node in xnl)
155             {
156                 co.BillToFaxNumber = node.InnerText;
157             }
158             xnl = xmldoc.GetElementsByTagName("BILL_TO_EMAIL_ADDRESS");
159             foreach (XmlNode node in xnl)
160             {
161                 co.BillToEmailAddress = node.InnerText;
162             }
163             xnl = xmldoc.GetElementsByTagName("BILL_TO_REF_NUM");
164             foreach (XmlNode node in xnl)
165             {
166                 co.BillToRefNum = node.InnerText;
167             }
168             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CONTACT_NAME");
169             foreach (XmlNode node in xnl)
170             {
171                 co.ShipToContactName = node.InnerText;
172             }
173             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_1");
174             foreach (XmlNode node in xnl)
175             {
176                 co.ShipToAddressLine1 = node.InnerText;
177             }
178             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_2");
179             foreach (XmlNode node in xnl)
180             {
181                 co.ShipToAddressLine2 = node.InnerText;
182             }
183             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_3");
184             foreach (XmlNode node in xnl)
185             {
186                 co.ShipToAddressLine3 = node.InnerText;
187             }
188             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CITY");
189             foreach (XmlNode node in xnl)
190             {
191                 co.ShipToCity = node.InnerText;
192             }
193             xnl = xmldoc.GetElementsByTagName("SHIP_TO_STATE");
194             foreach (XmlNode node in xnl)
195             {
196                 co.ShipToState = node.InnerText;
197             }
198             xnl = xmldoc.GetElementsByTagName("SHIP_TO_COUNTRY");
199             foreach (XmlNode node in xnl)
200             {
201                 co.ShipToCountry = node.InnerText;
202             }
203             xnl = xmldoc.GetElementsByTagName("SHIP_TO_POSTAL_CODE");
204             foreach (XmlNode node in xnl)
205             {
206                 co.ShipToPostalCode = node.InnerText;
207             }
208             xnl = xmldoc.GetElementsByTagName("SHIP_TO_PHONE_NUMBER");
209             foreach (XmlNode node in xnl)
210             {
211                 co.ShipToPhoneNumber = node.InnerText;
212             }
213             xnl = xmldoc.GetElementsByTagName("SHIP_TO_FAX_NUMBER");
214             foreach (XmlNode node in xnl)
215             {
216                 co.ShipToFaxNumber = node.InnerText;
217             }
218             xnl = xmldoc.GetElementsByTagName("SHIP_TO_EMAIL_ADDRESS");
219             foreach (XmlNode node in xnl)
220             {
221                 co.ShipToEmailAddress = node.InnerText;
222             }
223             xnl = xmldoc.GetElementsByTagName("SHIP_TO_REF_NUM");
224             foreach (XmlNode node in xnl)
225             {
226                 co.ShipToRefNum = node.InnerText;
227             }
228             xnl = xmldoc.GetElementsByTagName("PRIORITY_LEVEL");
229             foreach (XmlNode node in xnl)
230             {
231                 co.PriorityLevel = Convert.ToInt16(node.InnerText);
232             }
233             //output CO header to SQL before proceeding to the coitem
234             DatabaseFactory.WriteRecordCO(ref co);
235 
236             //build COITEM records, per line
237             xnl = xmldoc.GetElementsByTagName("Detail");
238             foreach (XmlNode node in xnl)
239             {
240                 coitem.CO_Line = Convert.ToInt16(node.ChildNodes[0].InnerText);
241                 coitem.Serial = node.ChildNodes[2].InnerText;
242                 coitem.Item = node.ChildNodes[3].InnerText;
243                 coitem.Smartpart = node.ChildNodes[4].InnerText;
244                 coitem.Desc = node.ChildNodes[5].InnerText;
245                 coitem.UnitPrice = Convert.ToDecimal(node.ChildNodes[7].InnerText);
246                 coitem.UnitCost = Convert.ToDecimal(node.ChildNodes[8].InnerText);
247                 coitem.Discount = Convert.ToDecimal(node.ChildNodes[9].InnerText);
248                 coitem.QTY = Convert.ToDecimal(node.ChildNodes[10].InnerText);
249                 coitem.PriorityLevel = co.PriorityLevel;
250                 globalOrderLineNum = coitem.CO_Line;
251                 //output coitem record
252                 DatabaseFactory.WriteRecordCOItem(ref coitem);
253 
254                 //*** Everything else here builds on the COITEM ***
255                 
256                 //iterate through Inputs for the line
257                 XmlNodeList xnli = xmldoc.GetElementsByTagName("Input");
258                 foreach (XmlNode nodei in xnli)
259                 {
260                     cfg.CO_Line = coitem.CO_Line;
261                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
262                     cfg.CValue = nodei.ChildNodes[0].InnerText;
263                     cfg.CType = nodei.ChildNodes[1].InnerText;
264                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
265                     cfg.PriorityLevel = co.PriorityLevel;
266                     //output cfg (parmval) record
267                     DatabaseFactory.WriteRecordCfg(ref cfg);
268                 }
269                 //item-master for the line we are working with
270                 int recordSequence = 1;
271                 XmlNodeList xnlim = xmldoc.GetElementsByTagName("ItemMaster");
272                 citem.CO_Line = coitem.CO_Line;
273                 foreach (XmlNode nodeim in xnlim)
274                 {
275                     citem.Sequence = recordSequence;
276                     citem.Smartpart = nodeim.ChildNodes[0].InnerText;
277                     citem.Item = nodeim.ChildNodes[1].InnerText;
278                     citem.Desc = nodeim.ChildNodes[3].InnerText;
279                     string cost = nodeim.ChildNodes[4].InnerText;
280                     string price = nodeim.ChildNodes[5].InnerText;
281                     string sell = nodeim.ChildNodes[6].InnerText;
282                     string weight = nodeim.ChildNodes[9].InnerText;
283                     citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
284                     citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
285                     citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
286                     citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
287                     citem.UnitOfMeasure = nodeim.ChildNodes[26].InnerText;
288                     citem.PriorityLevel = Convert.ToInt16(nodeim.ChildNodes[38].InnerText);
289                     recordSequence += 1;
290                     //output item-master record
291                     DatabaseFactory.WriteRecordCItem(ref citem);
292                 }
293                 //BOM records
294                 recordSequence = 0;
295                 XmlNodeList xnlb = xmldoc.GetElementsByTagName("Bom");
296                 bom.CO_Line = coitem.CO_Line;
297                 foreach (XmlNode nodeib in xnlb)
298                 {
299                     recordSequence = 1;
300                     bom.Sequence = recordSequence;
301                     var parent = nodeib.SelectSingleNode("..");
302                     string parentID = parent.ChildNodes[0].InnerText;
303                     bom.Parent = parentID == "1" ? null : parentID;
304                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
305                     bom.Item = nodeib.ChildNodes[1].InnerText;
306                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
307                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
308                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
309                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
310                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
311                     bom.PriorityLevel = co.PriorityLevel;
312                     //output BOM record
313                     DatabaseFactory.WriteRecordBOM(ref bom);
314                 }
315                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
316                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
317                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
318             }
319         }
320     }
321 }
322
