1 using System;
2 using System.Collections.Generic;
3 using System.Linq;
4 using System.Text;
5 //using System.Threading.Tasks;
6 using System.Xml;
7 
8 namespace ConfigureOneFlag
9 {
13     class StagingUtilities
14     {
15         public static void MapXMLToSQL(XmlDocument xmldoc)
16         {
17             zCfgCO co = new zCfgCO();
18             zCfgCOitem coitem = new zCfgCOitem();
19             zCfgItem citem = new zCfgItem();
20             zCfgParmVal cfg = new zCfgParmVal();
21             zCfgBOM bom = new zCfgBOM();
22 
23             //Load staging-table objects from XML
24             XmlNodeList xnl = xmldoc.GetElementsByTagName("ORDER_NUM");
25             foreach (XmlNode node in xnl)
26             {
27                 co.CO_Num = node.InnerText;
28                 coitem.CO_Num = node.InnerText;
29                 cfg.CO_Num = node.InnerText;
30                 citem.CO_Num = node.InnerText;
31                 bom.CO_Num = node.InnerText;
32             }
33             string logEvent;
34             switch (co.CO_Num == null || co.CO_Num == "")
35             {
36                 case true:
37                     logEvent = "ORDER NUMBER NOT FOUND: " + co.CO_Num;
38                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
39                     return;
40                 default:
41                     //Remove any pre-existing records in SQL for this order
42                     DatabaseFactory.CleanupOrder(co.CO_Num);
43                     break;
44             }
45             logEvent = "MAPPING XML TO STAGING TABLES";
46             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
47 
48             //build CO header
49             xnl = xmldoc.GetElementsByTagName("ID");
50             foreach (XmlNode node in xnl)
51             {
52                 co.Identifier = node.InnerText;
53                 break;          //we want first occurrence, ONLY
54             }
55             xnl = xmldoc.GetElementsByTagName("ORDER_REF_NUM");
56             foreach (XmlNode node in xnl)
57             {
58                 co.CORefNum = node.InnerText;
59             }
60             xnl = xmldoc.GetElementsByTagName("CUST_NAME");
61             foreach (XmlNode node in xnl)
62             {
63                 co.CustName = node.InnerText;
64             }
65             xnl = xmldoc.GetElementsByTagName("CUST_REF_NUM");
66             foreach (XmlNode node in xnl)
67             {
68                 co.CustRefNum = node.InnerText;
69             }
70             xnl = xmldoc.GetElementsByTagName("ACCOUNT_NUM");
71             foreach (XmlNode node in xnl)
72             {
73                 co.AccountNum = node.InnerText;
74             }
75             xnl = xmldoc.GetElementsByTagName("ERP_REFERENCE_NUM");
76             foreach (XmlNode node in xnl)
77             {
78                 co.ErpReferenceNum = node.InnerText;
79             }
80             xnl = xmldoc.GetElementsByTagName("PROJECT");
81             foreach (XmlNode node in xnl)
82             {
83                 co.Project = node.InnerText;
84             }
85             xnl = xmldoc.GetElementsByTagName("PAYMENT_TERMS");
86             foreach (XmlNode node in xnl)
87             {
88                 co.PaymentTerms = node.InnerText;
89             }
90             xnl = xmldoc.GetElementsByTagName("SHIP_VIA");
91             foreach (XmlNode node in xnl)
92             {
93                 co.ShipVia = node.InnerText;
94             }
95             xnl = xmldoc.GetElementsByTagName("SHIPPING_TERMS");
96             foreach (XmlNode node in xnl)
97             {
98                 co.ShippingTerms = node.InnerText;
99             }
100             xnl = xmldoc.GetElementsByTagName("BILL_TO_CONTACT_NAME");
101             foreach (XmlNode node in xnl)
102             {
103                 co.BillToContactName = node.InnerText;
104             }
105             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_1");
106             foreach (XmlNode node in xnl)
107             {
108                 co.BillToAddressLine1 = node.InnerText;
109             }
110             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_2");
111             foreach (XmlNode node in xnl)
112             {
113                 co.BillToAddressLine2 = node.InnerText;
114             }
115             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_3");
116             foreach (XmlNode node in xnl)
117             {
118                 co.BillToAddressLine3 = node.InnerText;
119             }
120             xnl = xmldoc.GetElementsByTagName("BILL_TO_CITY");
121             foreach (XmlNode node in xnl)
122             {
123                 co.BillToCity = node.InnerText;
124             }
125             xnl = xmldoc.GetElementsByTagName("BILL_TO_STATE");
126             foreach (XmlNode node in xnl)
127             {
128                 co.BillToState = node.InnerText;
129             }
130             xnl = xmldoc.GetElementsByTagName("BILL_TO_COUNTRY");
131             foreach (XmlNode node in xnl)
132             {
133                 co.BillToCountry = node.InnerText;
134             }
135             xnl = xmldoc.GetElementsByTagName("BILL_TO_POSTAL_CODE");
136             foreach (XmlNode node in xnl)
137             {
138                 co.BillToPostalCode = node.InnerText;
139             }
140             xnl = xmldoc.GetElementsByTagName("BILL_TO_PHONE_NUMBER");
141             foreach (XmlNode node in xnl)
142             {
143                 co.BillToPhoneNumber = node.InnerText;
144                 if (co.BillToPhoneNumber.Length == 0) { co.BillToPhoneNumber = "NA"; };
145             }
146             xnl = xmldoc.GetElementsByTagName("BILL_TO_FAX_NUMBER");
147             foreach (XmlNode node in xnl)
148             {
149                 co.BillToFaxNumber = node.InnerText;
150             }
151             xnl = xmldoc.GetElementsByTagName("BILL_TO_EMAIL_ADDRESS");
152             foreach (XmlNode node in xnl)
153             {
154                 co.BillToEmailAddress = node.InnerText;
155             }
156             xnl = xmldoc.GetElementsByTagName("BILL_TO_REF_NUM");
157             foreach (XmlNode node in xnl)
158             {
159                 co.BillToRefNum = node.InnerText;
160             }
161             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CONTACT_NAME");
162             foreach (XmlNode node in xnl)
163             {
164                 co.ShipToContactName = node.InnerText;
165             }
166             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_1");
167             foreach (XmlNode node in xnl)
168             {
169                 co.ShipToAddressLine1 = node.InnerText;
170             }
171             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_2");
172             foreach (XmlNode node in xnl)
173             {
174                 co.ShipToAddressLine2 = node.InnerText;
175             }
176             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_3");
177             foreach (XmlNode node in xnl)
178             {
179                 co.ShipToAddressLine3 = node.InnerText;
180             }
181             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CITY");
182             foreach (XmlNode node in xnl)
183             {
184                 co.ShipToCity = node.InnerText;
185             }
186             xnl = xmldoc.GetElementsByTagName("SHIP_TO_STATE");
187             foreach (XmlNode node in xnl)
188             {
189                 co.ShipToState = node.InnerText;
190             }
191             xnl = xmldoc.GetElementsByTagName("SHIP_TO_COUNTRY");
192             foreach (XmlNode node in xnl)
193             {
194                 co.ShipToCountry = node.InnerText;
195             }
196             xnl = xmldoc.GetElementsByTagName("SHIP_TO_POSTAL_CODE");
197             foreach (XmlNode node in xnl)
198             {
199                 co.ShipToPostalCode = node.InnerText;
200             }
201             xnl = xmldoc.GetElementsByTagName("SHIP_TO_PHONE_NUMBER");
202             foreach (XmlNode node in xnl)
203             {
204                 co.ShipToPhoneNumber = node.InnerText;
205             }
206             xnl = xmldoc.GetElementsByTagName("SHIP_TO_FAX_NUMBER");
207             foreach (XmlNode node in xnl)
208             {
209                 co.ShipToFaxNumber = node.InnerText;
210             }
211             xnl = xmldoc.GetElementsByTagName("SHIP_TO_EMAIL_ADDRESS");
212             foreach (XmlNode node in xnl)
213             {
214                 co.ShipToEmailAddress = node.InnerText;
215             }
216             xnl = xmldoc.GetElementsByTagName("SHIP_TO_REF_NUM");
217             foreach (XmlNode node in xnl)
218             {
219                 co.ShipToRefNum = node.InnerText;
220             }
221             xnl = xmldoc.GetElementsByTagName("PRIORITY_LEVEL");
222             foreach (XmlNode node in xnl)
223             {
224                 co.PriorityLevel = Convert.ToInt16(node.InnerText);
225             }
226             //output CO header to SQL before proceeding to the coitem
227             DatabaseFactory.WriteRecordCO(ref co);
228 
229             //build COITEM records, per line
230             xnl = xmldoc.GetElementsByTagName("Detail");
231             foreach (XmlNode node in xnl)
232             {
233                 coitem.CO_Line = Convert.ToInt16(node.ChildNodes[0].InnerText);
234                 coitem.Serial = node.ChildNodes[2].InnerText;
235                 coitem.Item = node.ChildNodes[3].InnerText;
236                 coitem.Smartpart = node.ChildNodes[4].InnerText;
237                 coitem.Desc = node.ChildNodes[5].InnerText;
238                 coitem.UnitPrice = Convert.ToDecimal(node.ChildNodes[7].InnerText);
239                 coitem.UnitCost = Convert.ToDecimal(node.ChildNodes[8].InnerText);
240                 coitem.Discount = Convert.ToDecimal(node.ChildNodes[9].InnerText);
241                 coitem.QTY = Convert.ToDecimal(node.ChildNodes[10].InnerText);
242                 coitem.PriorityLevel = co.PriorityLevel;
243 
244                 //output coitem record
245                 DatabaseFactory.WriteRecordCOItem(ref coitem);
246 
247                 //*** Everything else here builds on the COITEM ***
248 
249                 //iterate through Inputs for the line
250                 XmlNodeList xnli = xmldoc.GetElementsByTagName("Input");
251                 foreach (XmlNode nodei in xnli)
252                 {
253                     cfg.CO_Line = coitem.CO_Line;
254                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
255                     cfg.CValue = nodei.ChildNodes[0].InnerText;
256                     cfg.CType = nodei.ChildNodes[1].InnerText;
257                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
258                     cfg.PriorityLevel = co.PriorityLevel;
259                     //output cfg (parmval) record
260                     DatabaseFactory.WriteRecordCfg(ref cfg);
261                 }
262                 //item-master for the line we are working with
263                 int recordSequence = 1;
264                 XmlNodeList xnlim = xmldoc.GetElementsByTagName("ItemMaster");
265                 citem.CO_Line = coitem.CO_Line;
266                 foreach (XmlNode nodeim in xnlim)
267                 {
268                     citem.Sequence = recordSequence;
269                     citem.Smartpart = nodeim.ChildNodes[0].InnerText;
270                     citem.Item = nodeim.ChildNodes[1].InnerText;
271                     citem.Desc = nodeim.ChildNodes[3].InnerText;
272                     string cost = nodeim.ChildNodes[4].InnerText;
273                     string price = nodeim.ChildNodes[5].InnerText;
274                     string sell = nodeim.ChildNodes[6].InnerText;
275                     string weight = nodeim.ChildNodes[9].InnerText;
276                     citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
277                     citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
278                     citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
279                     citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
280                     citem.UnitOfMeasure = nodeim.ChildNodes[26].InnerText;
281                     citem.PriorityLevel = Convert.ToInt16(nodeim.ChildNodes[38].InnerText);
282                     recordSequence += 1;
283                     //output item-master record
284                     DatabaseFactory.WriteRecordCItem(ref citem);
285                 }
286                 //BOM records
287                 recordSequence = 0;
288                 XmlNodeList xnlb = xmldoc.GetElementsByTagName("Bom");
289                 bom.CO_Line = coitem.CO_Line;
290                 foreach (XmlNode nodeib in xnlb)
291                 {
292                     recordSequence = 1;
293                     bom.Sequence = recordSequence;
294                     var parent = nodeib.SelectSingleNode("..");
295                     string parentID = parent.ChildNodes[0].InnerText;
296                     bom.Parent = parentID == "1" ? null : parentID;
297                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
298                     bom.Item = nodeib.ChildNodes[1].InnerText;
299                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
300                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
301                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
302                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
303                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
304                     bom.PriorityLevel = co.PriorityLevel;
305                     //output BOM record
306                     DatabaseFactory.WriteRecordBOM(ref bom);
307                 }
308                 logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
309                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
310                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
311             }
312         }
313     }
314 }
315
