38                 XmlNode nodeSite = node.SelectSingleNode("//c1:Input[@name='ORDER_SITE']", nsmgr);
39                 try
40                 {
41                     dbSite = nodeSite.ChildNodes[0].Attributes["name"].InnerXml;
42                 }
43                 catch (Exception exSite)
44                 {
45                     Triggers.logEvent = "ERROR occurred: " + exSite.Message;
46                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
47                 }
48                 dbSite = nodeSite.ChildNodes[0].Attributes["name"].InnerXml;
49                 if (dbSite == null) { foundSite = false; }
50                 //foundSite &= dbSite != null;
51 
52                 switch (foundSite == true)
