141             string custSeq = "";
142             int sPos = co.ShipToRefNum.IndexOf("-", StringComparison.CurrentCulture);
143             custSeq = co.ShipToRefNum.Substring(sPos + 1, (co.ShipToRefNum.Length - (sPos + 1)));
144             string customerHoldReason = "";
145 
