147             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
148 
149             try
150             {
151                 customerHoldReason = DatabaseFactory.CustomerOnHold(co.CustRefNum, custSeq);
