153             catch (Exception ex9)
154             {
155                 Triggers.logEvent = "ERROR: " + ex9.Message + ".  Processing Aborted";
156                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
157                 SendMail.MailMessage(Triggers.logEvent, "C1 Processing Aborted");
