164                 Triggers.forceStop = 1;
165                 Triggers.logEvent = "Processing Aborted.  Customer " + co.ShipToRefNum + " Is On Hold: " + customerHoldReason;
166                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
167                 SendMail.MailMessage(Triggers.logEvent, "C1 Processing Aborted");
168                 return;
