259                 //co.EndUser = string.IsNullOrEmpty(nodeDRS.ChildNodes[0].Attributes["name"].InnerXml) ? null : nodeDRS.ChildNodes[0].Attributes["name"].InnerXml;
260                 co.EndUser = nodeDRS.ChildNodes[0].Attributes["name"].InnerXml;
261                 nodeDRS = nodeds.SelectSingleNode("//c1:Input[@name='ENGINEER']", nsmgr);
