263                 co.Engineer = nodeDRS.ChildNodes[0].Attributes["name"].InnerXml;
264 
265                 //evaluate CITY and then CIty if the former fails, as some XML is loaded improperly with mixed-case for City
266                 try
267                 {
268                     nodeDRS = nodeds.SelectSingleNode("//c1:Input[@name='DROP_SHIP_CITY']", nsmgr);
269                     co.DropShipCity = string.IsNullOrEmpty(nodeDRS.ChildNodes[0].Attributes["name"].InnerXml) ? " " : nodeDRS.ChildNodes[0].Attributes["name"].InnerXml;
270                 }
271                 catch (Exception dd1)
272                 {
273                     try
274                     {
275                         nodeDRS = nodeds.SelectSingleNode("//c1:Input[@name='DROP_SHIP_CIty']", nsmgr);
276                         co.DropShipCity = string.IsNullOrEmpty(nodeDRS.ChildNodes[0].Attributes["name"].InnerXml) ? " " : nodeDRS.ChildNodes[0].Attributes["name"].InnerXml;
277                     }
278                     catch (Exception dd2)
279                     {
280                         co.DropShipCity = " ";
281                     }
282                 }
283                 
284                 nodeDRS = nodeds.SelectSingleNode("//c1:Input[@name='DROP_SHIP_STATE']", nsmgr);
285                 co.DropShipState = string.IsNullOrEmpty(nodeDRS.ChildNodes[0].Attributes["name"].InnerXml) ? " " : nodeDRS.ChildNodes[0].Attributes["name"].InnerXml;
286                 nodeDRS = nodeds.SelectSingleNode("//c1:Input[@name='DROP_SHIP_ZIP_CODE']", nsmgr);
