299             //Replace &amp; in any dropship fields with &
300             co.DropShipName = co.DropShipName.Replace("&amp;", "&");
301             co.DropShipAddress1 = co.DropShipAddress1.Replace("&amp;", "&");
302             co.DropShipAddress2 = co.DropShipAddress2.Replace("&amp;", "&");
