307             //build COITEM records, per line
308             XmlNodeList xnl = xmldoc.GetElementsByTagName("Detail");
309             foreach (XmlNode node in xnl)
310             {
311                 XmlNode nodertv = node.SelectSingleNode("c1:ORDER_LINE_NUM", nsmgr);
312                 coitem.CO_Line = Convert.ToInt16(nodertv.ChildNodes[0].InnerText);
313                 nodertv = node.SelectSingleNode("c1:SERIAL_NUM", nsmgr); 
314                 coitem.Serial = string.IsNullOrEmpty(nodertv.ChildNodes[0].InnerText) ? " " : nodertv.ChildNodes[0].InnerText;
315 
