320                     nodertv = node.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
321                     coitem.Smartpart = string.IsNullOrEmpty(nodertv.ChildNodes[0].InnerText) ? " " : nodertv.ChildNodes[0].InnerText;
322                 }
323                 catch (Exception c11)
324                 {
325                     coitem.Item = " ";
326                     coitem.Smartpart = " ";
327                 }
328 
329                 if (coitem.Item == "") { coitem.Item = " "; }
330                 if (coitem.Smartpart == "") { coitem.Smartpart = " "; }
331 
332                 nodertv = node.SelectSingleNode("c1:DESCRIPTION", nsmgr);
333                 coitem.Desc = string.IsNullOrEmpty(nodertv.ChildNodes[0].InnerText) ? " " : nodertv.ChildNodes[0].InnerText;
334                 nodertv = node.SelectSingleNode("c1:TYPE", nsmgr);
335                 coitem.ConfigType = string.IsNullOrEmpty(nodertv.ChildNodes[0].InnerText) ? " " : nodertv.ChildNodes[0].Value;
336                 nodertv = node.SelectSingleNode("c1:UNIT_PRICE", nsmgr);
337                 coitem.UnitPrice = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
338                 nodertv = node.SelectSingleNode("c1:UNIT_COST", nsmgr);
339                 coitem.UnitCost = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
340                 nodertv = node.SelectSingleNode("c1:DISCOUNT_AMT", nsmgr);
341                 coitem.Discount = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
342                 nodertv = node.SelectSingleNode("c1:QUANTITY", nsmgr);
343                 coitem.QTY = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
344                 coitem.PriorityLevel = co.PriorityLevel;
345                 globalOrderLineNum = coitem.CO_Line;
346                 coitem.OrderLineNotes = " ";
347                 
348                 //Look for Line Notes, load into COItem
349                 try
350                 {
351                     nodertv = node.SelectSingleNode("c1:Input[@name='LINE_NOTES']", nsmgr);
352                     coitem.OrderLineNotes = nodertv.ChildNodes[0].Attributes["name"].InnerXml.Length == 0 ? " " : nodertv.ChildNodes[0].Attributes["name"].InnerXml;
353                     coitem.OrderLineNotes = coitem.OrderLineNotes.Replace("&amp;", "&");
354                 }
355                 catch (Exception exrtv)
356                 {
357                     Triggers.logEvent = "Line_Note error: " + exrtv.Message;
358                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
359                 }
360 
361                 if (coitem.ConfigType == "K")
362                 {
363                     Triggers.logEvent = "WARNING: Config Type Is: " + coitem.ConfigType + " On C1 Order#: " + co.CO_Num + ". Ignoring line# " + coitem.CO_Line;
364                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
365                     SendMail.MailMessage(Triggers.logEvent, "Config-Type Warning");
366                     continue;           //we cannot process a type K; notify, ignore this line and continue with the next detail node if any exist
367                 }
368 
369                 //output coitem record
370                 DatabaseFactory.WriteRecordCOItem(ref coitem);
371 
372                 //*** Everything else here builds on the COITEM ***
373                 var detailParent = node.SelectSingleNode(".");     //ensure we traverse ONLY children of this node (Detail) as the new parent (root) element
374                 XmlDocument detailDoc = new XmlDocument();
375                 detailDoc.LoadXml(detailParent.OuterXml);
376 
377                 //iterate through Inputs for the line (none of these have static variable names or attributes)
378                 XmlNodeList xnli = detailDoc.GetElementsByTagName("Input");
379                 foreach (XmlNode nodei in xnli)
380                 {
381                     cfg.CO_Line = coitem.CO_Line;
382                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
383                     cfg.CValue = nodei.ChildNodes[0].InnerText;
384                     cfg.CType = nodei.ChildNodes[1].InnerText;
385                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
386                     cfg.PriorityLevel = co.PriorityLevel;
387                     //output cfg (parmval) record
388                     DatabaseFactory.WriteRecordCfg(ref cfg);
389                 }
390 
391                 //Look for SHIP_VIA in INPUTS 
392                 co.ShipVia = " ";
393                 XmlNodeList xnlisv = xmldoc.GetElementsByTagName("Input");
394                 foreach (XmlNode nodeisv in xnlisv)
395                 {
396                     if (nodeisv.ChildNodes[2].InnerText == "SHIP_VIA") { co.ShipVia = nodeisv.ChildNodes[0].Attributes["name"].Value; }
397                 }
398                 
399                 //item-master for the line we are working with
400                 int recordSequence = 1;
401                 XmlNodeList xnlim = detailDoc.GetElementsByTagName("ItemMaster");
402                 citem.CO_Line = coitem.CO_Line;
403                 string cost = "";
404                 string price = "";
405                 string sell = "";
406                 string weight = "";
407                 citem.IM_VAR1 = "";
408                 citem.IM_VAR2 = "";
409                 citem.IM_VAR3 = "";
410                 citem.IM_VAR4 = "";
411                 citem.IM_VAR5 = "";
412                 //Build nodelist of ItemMaster nodes and a second nodelist within of its childnodes so we can reference those elements by name instead of index
413                 foreach (XmlNode nodeim in xnlim)
414                 {
415                     foreach (XmlNode childIM in nodeim.ChildNodes)
416                     {
417                         switch (childIM.Name)
418                         {
419                             case "SMARTPART_NUM":
420                                 citem.Smartpart = childIM.InnerText;
421                                 break;
422                             case "ITEM_NUM":
423                                 citem.Item = childIM.InnerText;
424                                 break;
425                             case "DESCRIPTION":
426                                 citem.Desc = childIM.InnerText;
427                                 break;
428                             case "COST":
429                                 cost = childIM.InnerText;
430                                 break;
431                             case "PRICE":
432                                 price = childIM.InnerText;
433                                 break;
434                             case "SELL_PRICE":
435                                 sell = childIM.InnerText;
436                                 break;
437                             case "WEIGHT":
438                                 weight = childIM.InnerText;
439                                 break;
440                             case "UOM":
441                                 citem.UnitOfMeasure = childIM.InnerText;
442                                 break;
443                             case "PRIORITY_LEVEL":
444                                 citem.PriorityLevel = Convert.ToInt16(childIM.InnerText);
445                                 break;
446                             case "VAR_1":
447                                 citem.IM_VAR1 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
448                                 break;
449                             case "VAR_2":
450                                 citem.IM_VAR2 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
451                                 break;
452                             case "VAR_3":
453                                 citem.IM_VAR3 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
454                                 break;
455                             case "VAR_4":
456                                 citem.IM_VAR4 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
457                                 break;
458                             case "VAR_5":
459                                 citem.IM_VAR5 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
460                                 break;
461                             default:
462                                 //do nothing
463                                 break;
464                         }
465                         citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
466                         citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
467                         citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
468                         citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
469                         citem.Sequence = recordSequence;
470                     }
471                     recordSequence += 1;
472                     DatabaseFactory.WriteRecordCItem(ref citem);
473                     cost = "";
474                     price = "";
475                     sell = "";
476                     weight = "";
477                     citem.IM_VAR1 = "";
478                     citem.IM_VAR2 = "";
479                     citem.IM_VAR3 = "";
480                     citem.IM_VAR4 = "";
481                     citem.IM_VAR5 = "";
482                 }
483 
484                 //BOM records (Must load in this manner, as none of these have static variable names or attributes)
485                 recordSequence = 0;
486                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
487                 bom.CO_Line = coitem.CO_Line;
488                 foreach (XmlNode nodeib in xnlb)
489                 {
490                     recordSequence += 1;
491                     bom.Sequence = recordSequence;
492                     bom.RecordSequence = recordSequence;
493                     var parent = nodeib.SelectSingleNode("..");
494                     string parentID = parent.ChildNodes[0].InnerText;
495                     bom.Parent = parentID == "1" ? null : parentID;
496                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
497                     bom.Item = nodeib.ChildNodes[1].InnerText;
498                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
499                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
500                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
501                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
502                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
503                     
504                     //Search the ItemMaster XML records for the matching smartpart_num and retrieve its priority level for this BOM record
505                     bool foundchild = false;
506                     bool imdone = false;
507                     foreach (XmlNode nodeIMPL in xnlim)
508                     {
509                         if (imdone == true)
510                         {
511                             break;
512                         }
513                         foreach (XmlNode childIMPL in nodeIMPL.ChildNodes)
514                         {
515                             if (imdone == true)
516                             {
517                                 break;
518                             }
519                             if (childIMPL.InnerText == bom.Smartpart)
520                             {
521                                 foundchild = true;
522                             }
523                             if (foundchild == true)
524                             {
525                                 if (childIMPL.Name == "PRIORITY_LEVEL")
526                                 {
527                                     bom.PriorityLevel = Convert.ToInt16(childIMPL.InnerText);
528                                     imdone = true;
529                                 }
530                             }
531                         }
532                     }
533                     //output BOM record
534                     DatabaseFactory.WriteRecordBOM(ref bom);
535                 }
536                 //Routing - Output only if the Routing tag contains a nodelist  count greater than 0
537                 Triggers.logEvent = "STARTING ROUTING LOGIC";
538                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
539                 int routebomSeq = 0;
540                 bool UseRouteBOM = false;
541                 route.CO_Num = coitem.CO_Num;
542                 route.CO_Line = coitem.CO_Line;
543                 XmlNodeList xnlr = detailDoc.GetElementsByTagName("Routing");
544                 if (xnlr.Count > 0)
545                 {
546                     UseRouteBOM = true;
547                     foreach (XmlNode nodecr in xnlr)
548                     {
549                         XmlNode nodecrtg = nodecr.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
550                         route.SmartpartNum = nodecrtg.ChildNodes[0].InnerText;
551                         nodecrtg = nodecr.SelectSingleNode("c1:ITEM_NUM", nsmgr);
552                         route.ItemNum = nodecrtg.ChildNodes[0].InnerText;
553                         nodecrtg = nodecr.SelectSingleNode("c1:BOM_ID", nsmgr);
554                         route.BOM_ID = nodecrtg.ChildNodes[0].InnerText;
555 
556                         //isolate OPERATION elements from Routing and process
557                         XmlDocument routeOP = new XmlDocument();
558                         routeOP.LoadXml(nodecr.OuterXml);
559                         XmlNodeList xnlOperation = routeOP.GetElementsByTagName("Operation");
560                         foreach (XmlNode nodeol in xnlOperation)
561                         {
562                             XmlNode operationParentTL = nodeol.SelectSingleNode(".");             //current operation becomes our new parent (.. to .)
563                             XmlDocument operationDocumentTL = new XmlDocument();
564                             operationDocumentTL.LoadXml(operationParentTL.OuterXml);
565                             XmlNodeList xnlOP = operationDocumentTL.GetElementsByTagName("OperationParam");
566                             foreach (XmlNode nodeParamChild in xnlOP)
567                             {
568                                 if (nodeParamChild.ChildNodes[0].InnerText == "LABOR_HRS") { route.Labor_Hours = Convert.ToDouble(nodeParamChild.ChildNodes[2].InnerText); }
569                                 if (nodeParamChild.ChildNodes[0].InnerText == "SETUP_HRS") { route.Setup_Hours = Convert.ToDouble(nodeParamChild.ChildNodes[2].InnerText); }
570                                 if (nodeParamChild.ChildNodes[0].InnerText == "WC") { route.WC = nodeParamChild.ChildNodes[2].InnerText; }
571                                 if (nodeParamChild.ChildNodes[0].InnerText == "NOTES") { route.Notes = nodeParamChild.ChildNodes[2].InnerText; }
572                                 if (nodeParamChild.ChildNodes[0].InnerText == "MACH_NAME") { route.Machine_Name = nodeParamChild.ChildNodes[2].InnerText; }
573                             }
574 
575                             XmlNode nodeOpItem = nodeol.SelectSingleNode("c1:OPER_NUM", nsmgr);
576                             route.OPERATION = Convert.ToInt16(nodeOpItem.ChildNodes[0].InnerText);
577                             nodeOpItem = nodeol.SelectSingleNode("c1:DESCRIPTION", nsmgr);
578                             route.Description = nodeOpItem.ChildNodes[0].InnerText;
579 
580                             //set current operation as new parent and look ONLY for its OperationInput tags
581                             var operationParent = nodeol.SelectSingleNode(".");     //ensure we traverse ONLY descendants of this node (Operation) as the new parent 
582                             
583                             XmlDocument operationDoc = new XmlDocument();
584                             operationDoc.LoadXml(operationParent.OuterXml);
585                             XmlNodeList xnlOperationInputs = operationDoc.GetElementsByTagName("OperationInput");
586 
587                             foreach (XmlNode nodeOpInput in xnlOperationInputs)
588                             {
589                                 XmlNode nodeoin = nodeOpInput.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
590                                 route.MatlSmartpartNum = nodeOpInput.ChildNodes[0].InnerText;
591                                 route.MatlItemNum = nodeOpInput.ChildNodes[1].InnerText;
592                                 route.MatlQty = Convert.ToDecimal(nodeOpInput.ChildNodes[2].InnerText);
593                                 //write to gr_cfgroutebom
594                                 routebomSeq += 1;
595                                 route.Seq = routebomSeq;
596                                 DatabaseFactory.WriteRecordCfgRoute(ref route);
597                             }
598                         }
599                     }
600                 }
601 
602                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
603                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
604                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
605 
606                 //If we used routeBOM, dump the old-process BOM records
607                 //if (UseRouteBOM) { DatabaseFactory.DeleteBOM(co.CORefNum); }          //this may be reinstated in the future
608             }
609             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
610         }
611         public static string LoadFromXML(XmlDocument xmldoc, string element, XmlNamespaceManager nsmgr)
612         {
613             string returnValue = "";
614             XmlNode xnode = xmldoc.SelectSingleNode(element, nsmgr);
615             returnValue = string.IsNullOrEmpty(xnode.InnerText) ? " " : xnode.InnerText;
616             return returnValue;
617         }
618     }
619 }
