560                     citem.IM_VAR3 = "";
561                     citem.IM_VAR4 = "";
562                     citem.IM_VAR5 = "";
563                 }
564 
565                 //BOM records
566                 recordSequence = 0;
567                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
568                 bom.CO_Line = coitem.CO_Line;
569                 foreach (XmlNode nodeib in xnlb)
570                 {
571                     recordSequence += 1;
572                     bom.Sequence = recordSequence;
573                     bom.RecordSequence = recordSequence;
574                     var parent = nodeib.SelectSingleNode("..");
575                     string parentID = parent.ChildNodes[0].InnerText;
576                     bom.Parent = parentID == "1" ? null : parentID;
577                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
578                     bom.Item = nodeib.ChildNodes[1].InnerText;
579                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
580                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
581                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
582                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
583                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
584 
585                     //Search the ItemMaster XML records for the matching smartpart_num and retrieve its priority level for this BOM record
586                     bool foundchild = false;
587                     bool imdone = false;
588                     foreach (XmlNode nodeIMPL in xnlim)
589                     {
590                         if (imdone == true)
591                         {
592                             break;
593                         }
594                         foreach (XmlNode childIMPL in nodeIMPL.ChildNodes)
595                         {
596                             if (imdone == true)
597                             {
598                                 break;
599                             }
600                             if (childIMPL.InnerText == bom.Smartpart)
601                             {
602                                 foundchild = true;
603                             }
604                             if (foundchild == true)
605                             {
606                                 if (childIMPL.Name == "PRIORITY_LEVEL")
607                                 {
608                                     bom.PriorityLevel = Convert.ToInt16(childIMPL.InnerText);
609                                     imdone = true;
610                                 }
611                             }
612                         }
613                     }
614                     //output BOM record
615                     DatabaseFactory.WriteRecordBOM(ref bom);
616                 }
617                 
618                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
619                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
620                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
621             }
622             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
623         }
624     }
625 }
