1 using System;
2 using System.Xml;
3 
4 namespace ConfigureOneFlag
5 {
9     class StagingUtilities
10     {
11         public static string globalOrderNum;
12         public static int globalOrderLineNum;
13                 
14         public static void MapXMLToSQL(XmlDocument xmldoc)
15         {
16             zCfgCO co = new zCfgCO();
17             zCfgCOitem coitem = new zCfgCOitem();
18             zCfgItem citem = new zCfgItem();
19             zCfgParmVal cfg = new zCfgParmVal();
20             zCfgBOM bom = new zCfgBOM();
21 
22             Audit.resetmE = true;       //reset the mE array in case we have any mapping errors to report for this cycle
23 
24             //Load staging-table objects from XML
25             XmlNodeList xnl = xmldoc.GetElementsByTagName("ORDER_NUM");
26             foreach (XmlNode node in xnl)
27             {
28                 co.CO_Num = node.InnerText;
29                 coitem.CO_Num = node.InnerText;
30                 cfg.CO_Num = node.InnerText;
31                 citem.CO_Num = node.InnerText;
32                 bom.CO_Num = node.InnerText;
33                 globalOrderNum = co.CO_Num;
34                 globalOrderLineNum = 0;
35             }
36             
37             switch (co.CO_Num == null || co.CO_Num == "")
38             {
39                 case true:
40                     Triggers.logEvent = "ORDER NUMBER NOT FOUND: " + co.CO_Num;
41                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
42                     return;
43                 default:
44                     //Remove any pre-existing records in SQL for this order
45                     DatabaseFactory.CleanupOrder(co.CO_Num);
46                     break;
47             }
48             Triggers.logEvent = "MAPPING XML TO STAGING TABLES";
49             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
50 
51             //build CO header
52             xnl = xmldoc.GetElementsByTagName("ID");
53             foreach (XmlNode node in xnl)
54             {
55                 co.Identifier = node.InnerText;
56                 break;          //we want first occurrence, ONLY
57             }
58             xnl = xmldoc.GetElementsByTagName("ORDER_REF_NUM");
59             foreach (XmlNode node in xnl)
60             {
61                 co.CORefNum = node.InnerText;
62             }
63             xnl = xmldoc.GetElementsByTagName("CUST_NAME");
64             foreach (XmlNode node in xnl)
65             {
66                 co.CustName = node.InnerText;
67             }
68             xnl = xmldoc.GetElementsByTagName("CUST_REF_NUM");
69             foreach (XmlNode node in xnl)
70             {
71                 co.CustRefNum = node.InnerText;
72             }
73             xnl = xmldoc.GetElementsByTagName("ACCOUNT_NUM");
74             foreach (XmlNode node in xnl)
75             {
76                 co.AccountNum = node.InnerText;
77             }
78             xnl = xmldoc.GetElementsByTagName("ERP_REFERENCE_NUM");
79             foreach (XmlNode node in xnl)
80             {
81                 co.ErpReferenceNum = node.InnerText;
82             }
83             xnl = xmldoc.GetElementsByTagName("PROJECT");
84             foreach (XmlNode node in xnl)
85             {
86                 co.Project = node.InnerText;
87             }
88             xnl = xmldoc.GetElementsByTagName("PAYMENT_TERMS");
89             foreach (XmlNode node in xnl)
90             {
91                 co.PaymentTerms = node.InnerText;
92             }
93             xnl = xmldoc.GetElementsByTagName("SHIP_VIA");
94             foreach (XmlNode node in xnl)
95             {
96                 co.ShipVia = node.InnerText;
97             }
98             xnl = xmldoc.GetElementsByTagName("SHIPPING_TERMS");
99             foreach (XmlNode node in xnl)
100             {
101                 co.ShippingTerms = node.InnerText;
102             }
103             xnl = xmldoc.GetElementsByTagName("BILL_TO_CONTACT_NAME");
104             foreach (XmlNode node in xnl)
105             {
106                 co.BillToContactName = node.InnerText;
107             }
108             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_1");
109             foreach (XmlNode node in xnl)
110             {
111                 co.BillToAddressLine1 = node.InnerText;
112             }
113             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_2");
114             foreach (XmlNode node in xnl)
115             {
116                 co.BillToAddressLine2 = node.InnerText;
117             }
118             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_3");
119             foreach (XmlNode node in xnl)
120             {
121                 co.BillToAddressLine3 = node.InnerText;
122             }
123             xnl = xmldoc.GetElementsByTagName("BILL_TO_CITY");
124             foreach (XmlNode node in xnl)
125             {
126                 co.BillToCity = node.InnerText;
127             }
128             xnl = xmldoc.GetElementsByTagName("BILL_TO_STATE");
129             foreach (XmlNode node in xnl)
130             {
131                 co.BillToState = node.InnerText;
132             }
133             xnl = xmldoc.GetElementsByTagName("BILL_TO_COUNTRY");
134             foreach (XmlNode node in xnl)
135             {
136                 co.BillToCountry = node.InnerText;
137             }
138             xnl = xmldoc.GetElementsByTagName("BILL_TO_POSTAL_CODE");
139             foreach (XmlNode node in xnl)
140             {
141                 co.BillToPostalCode = node.InnerText;
142             }
143             xnl = xmldoc.GetElementsByTagName("BILL_TO_PHONE_NUMBER");
144             foreach (XmlNode node in xnl)
145             {
146                 co.BillToPhoneNumber = node.InnerText;
147                 if (co.BillToPhoneNumber.Length == 0) { co.BillToPhoneNumber = "NA"; };
148             }
149             xnl = xmldoc.GetElementsByTagName("BILL_TO_FAX_NUMBER");
150             foreach (XmlNode node in xnl)
151             {
152                 co.BillToFaxNumber = node.InnerText;
153             }
154             xnl = xmldoc.GetElementsByTagName("BILL_TO_EMAIL_ADDRESS");
155             foreach (XmlNode node in xnl)
156             {
157                 co.BillToEmailAddress = node.InnerText;
158             }
159             xnl = xmldoc.GetElementsByTagName("BILL_TO_REF_NUM");
160             foreach (XmlNode node in xnl)
161             {
162                 co.BillToRefNum = node.InnerText;
163             }
164             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CONTACT_NAME");
165             foreach (XmlNode node in xnl)
166             {
167                 co.ShipToContactName = node.InnerText;
168             }
169             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_1");
170             foreach (XmlNode node in xnl)
171             {
172                 co.ShipToAddressLine1 = node.InnerText;
173             }
174             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_2");
175             foreach (XmlNode node in xnl)
176             {
177                 co.ShipToAddressLine2 = node.InnerText;
178             }
179             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_3");
180             foreach (XmlNode node in xnl)
181             {
182                 co.ShipToAddressLine3 = node.InnerText;
183             }
184             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CITY");
185             foreach (XmlNode node in xnl)
186             {
187                 co.ShipToCity = node.InnerText;
188             }
189             xnl = xmldoc.GetElementsByTagName("SHIP_TO_STATE");
190             foreach (XmlNode node in xnl)
191             {
192                 co.ShipToState = node.InnerText;
193             }
194             xnl = xmldoc.GetElementsByTagName("SHIP_TO_COUNTRY");
195             foreach (XmlNode node in xnl)
196             {
197                 co.ShipToCountry = node.InnerText;
198             }
199             xnl = xmldoc.GetElementsByTagName("SHIP_TO_POSTAL_CODE");
200             foreach (XmlNode node in xnl)
201             {
202                 co.ShipToPostalCode = node.InnerText;
203             }
204             xnl = xmldoc.GetElementsByTagName("SHIP_TO_PHONE_NUMBER");
205             foreach (XmlNode node in xnl)
206             {
207                 co.ShipToPhoneNumber = node.InnerText;
208             }
209             xnl = xmldoc.GetElementsByTagName("SHIP_TO_FAX_NUMBER");
210             foreach (XmlNode node in xnl)
211             {
212                 co.ShipToFaxNumber = node.InnerText;
213             }
214             xnl = xmldoc.GetElementsByTagName("SHIP_TO_EMAIL_ADDRESS");
215             foreach (XmlNode node in xnl)
216             {
217                 co.ShipToEmailAddress = node.InnerText;
218             }
219             xnl = xmldoc.GetElementsByTagName("SHIP_TO_REF_NUM");
220             foreach (XmlNode node in xnl)
221             {
222                 co.ShipToRefNum = node.InnerText;
223             }
224             xnl = xmldoc.GetElementsByTagName("PRIORITY_LEVEL");
225             foreach (XmlNode node in xnl)
226             {
227                 co.PriorityLevel = Convert.ToInt16(node.InnerText);
228             }
229             //output CO header to SQL before proceeding to the coitem
230             DatabaseFactory.WriteRecordCO(ref co);
231 
232             //build COITEM records, per line
233             xnl = xmldoc.GetElementsByTagName("Detail");
234             foreach (XmlNode node in xnl)
235             {
236                 coitem.CO_Line = Convert.ToInt16(node.ChildNodes[0].InnerText);
237                 coitem.Serial = node.ChildNodes[2].InnerText;
238                 coitem.Item = node.ChildNodes[3].InnerText;
239                 coitem.Smartpart = node.ChildNodes[4].InnerText;
240                 coitem.Desc = node.ChildNodes[5].InnerText;
241                 coitem.UnitPrice = Convert.ToDecimal(node.ChildNodes[7].InnerText);
242                 coitem.UnitCost = Convert.ToDecimal(node.ChildNodes[8].InnerText);
243                 coitem.Discount = Convert.ToDecimal(node.ChildNodes[9].InnerText);
244                 coitem.QTY = Convert.ToDecimal(node.ChildNodes[10].InnerText);
245                 coitem.PriorityLevel = co.PriorityLevel;
246                 globalOrderLineNum = coitem.CO_Line;
247                 //output coitem record
248                 DatabaseFactory.WriteRecordCOItem(ref coitem);
249 
250                 //*** Everything else here builds on the COITEM ***
251                 
252                 //iterate through Inputs for the line
253                 XmlNodeList xnli = xmldoc.GetElementsByTagName("Input");
254                 foreach (XmlNode nodei in xnli)
255                 {
256                     cfg.CO_Line = coitem.CO_Line;
257                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
258                     cfg.CValue = nodei.ChildNodes[0].InnerText;
259                     cfg.CType = nodei.ChildNodes[1].InnerText;
260                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
261                     cfg.PriorityLevel = co.PriorityLevel;
262                     //output cfg (parmval) record
263                     DatabaseFactory.WriteRecordCfg(ref cfg);
264                 }
265                 //item-master for the line we are working with
266                 int recordSequence = 1;
267                 XmlNodeList xnlim = xmldoc.GetElementsByTagName("ItemMaster");
268                 citem.CO_Line = coitem.CO_Line;
269                 foreach (XmlNode nodeim in xnlim)
270                 {
271                     citem.Sequence = recordSequence;
272                     citem.Smartpart = nodeim.ChildNodes[0].InnerText;
273                     citem.Item = nodeim.ChildNodes[1].InnerText;
274                     citem.Desc = nodeim.ChildNodes[3].InnerText;
275                     string cost = nodeim.ChildNodes[4].InnerText;
276                     string price = nodeim.ChildNodes[5].InnerText;
277                     string sell = nodeim.ChildNodes[6].InnerText;
278                     string weight = nodeim.ChildNodes[9].InnerText;
279                     citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
280                     citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
281                     citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
282                     citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
283                     citem.UnitOfMeasure = nodeim.ChildNodes[26].InnerText;
284                     citem.PriorityLevel = Convert.ToInt16(nodeim.ChildNodes[38].InnerText);
285                     recordSequence += 1;
286                     //output item-master record
287                     DatabaseFactory.WriteRecordCItem(ref citem);
288                 }
289                 //BOM records
290                 recordSequence = 0;
291                 XmlNodeList xnlb = xmldoc.GetElementsByTagName("Bom");
292                 bom.CO_Line = coitem.CO_Line;
293                 foreach (XmlNode nodeib in xnlb)
294                 {
295                     recordSequence = 1;
296                     bom.Sequence = recordSequence;
297                     var parent = nodeib.SelectSingleNode("..");
298                     string parentID = parent.ChildNodes[0].InnerText;
299                     bom.Parent = parentID == "1" ? null : parentID;
300                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
301                     bom.Item = nodeib.ChildNodes[1].InnerText;
302                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
303                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
304                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
305                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
306                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
307                     bom.PriorityLevel = co.PriorityLevel;
308                     //output BOM record
309                     DatabaseFactory.WriteRecordBOM(ref bom);
310                 }
311                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
312                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
313                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
314             }
315         }
316     }
317 }
