1 using System;
2 using System.Xml;
3 
4 namespace ConfigureOneFlag
5 {
9     class StagingUtilities
10     {
11         public static string globalOrderNum;
12         public static int globalOrderLineNum;
13                 
14         public static void MapXMLToSQL(XmlDocument xmldoc)
15         {
16             zCfgCO co = new zCfgCO();
17             zCfgCOitem coitem = new zCfgCOitem();
18             zCfgItem citem = new zCfgItem();
19             zCfgParmVal cfg = new zCfgParmVal();
20             zCfgBOM bom = new zCfgBOM();
21 
22             Audit.resetmE = true;       //reset the mE array in case we have any mapping errors to report for this cycle
23 
24             //*** Determine what site we are working with and re-set the connection string accordingly as far as the database name, else default to NOVB
25             XmlNodeList xnlsite = xmldoc.GetElementsByTagName("Input");
26             foreach (XmlNode node in xnlsite)
27             {
28                 switch (node.ChildNodes[2].InnerText == "ORDER SITE")
29                 {
30                     case true:
31                         string rplConnectionString = DatabaseFactory.connectionString;
32                         int csPos = rplConnectionString.IndexOf("NOVB");
33                         DatabaseFactory.connectionString = rplConnectionString.Substring(0, csPos) + node.ChildNodes[0].Attributes["name"].Value + rplConnectionString.Substring(csPos + 4, rplConnectionString.Length - (csPos + 4));
34                         Triggers.logEvent = "Connection String: " + DatabaseFactory.connectionString;
35                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
36                         break;
37                     default:
38                         //continue with our NOVB default
39                         break;
40                 }
41             }
42             
43             //Load staging-table objects from XML
44             XmlNodeList xnl = xmldoc.GetElementsByTagName("ORDER_NUM");
45             foreach (XmlNode node in xnl)
46             {
47                 co.CO_Num = node.InnerText;
48                 coitem.CO_Num = node.InnerText;
49                 cfg.CO_Num = node.InnerText;
50                 citem.CO_Num = node.InnerText;
51                 bom.CO_Num = node.InnerText;
52                 globalOrderNum = co.CO_Num;
53                 globalOrderLineNum = 0;
54             }
55             
56             switch (co.CO_Num == null || co.CO_Num == "")
57             {
58                 case true:
59                     Triggers.logEvent = "ORDER NUMBER NOT FOUND: " + co.CO_Num;
60                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
61                     return;
62                 default:
63                     //Remove any pre-existing records in SQL for this order
64                     DatabaseFactory.CleanupOrder(co.CO_Num);
65                     break;
66             }
67             Triggers.logEvent = "MAPPING XML TO STAGING TABLES";
68             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
69 
70             //build CO header
71             xnl = xmldoc.GetElementsByTagName("ID");
72             foreach (XmlNode node in xnl)
73             {
74                 co.Identifier = node.InnerText;
75                 break;          //we want first occurrence, ONLY
76             }
77             xnl = xmldoc.GetElementsByTagName("ORDER_REF_NUM");
78             foreach (XmlNode node in xnl)
79             {
80                 co.CORefNum = node.InnerText;
81             }
82             xnl = xmldoc.GetElementsByTagName("CUST_NAME");
83             foreach (XmlNode node in xnl)
84             {
85                 co.CustName = node.InnerText;
86             }
87             xnl = xmldoc.GetElementsByTagName("CUST_REF_NUM");
88             foreach (XmlNode node in xnl)
89             {
90                 co.CustRefNum = node.InnerText;
91             }
92             xnl = xmldoc.GetElementsByTagName("ACCOUNT_NUM");
93             foreach (XmlNode node in xnl)
94             {
95                 co.AccountNum = node.InnerText;
96             }
97             xnl = xmldoc.GetElementsByTagName("ERP_REFERENCE_NUM");
98             foreach (XmlNode node in xnl)
99             {
100                 co.ErpReferenceNum = node.InnerText;
101             }
102             xnl = xmldoc.GetElementsByTagName("PROJECT");
103             foreach (XmlNode node in xnl)
104             {
105                 co.Project = node.InnerText;
106             }
107             xnl = xmldoc.GetElementsByTagName("PAYMENT_TERMS");
108             foreach (XmlNode node in xnl)
109             {
110                 co.PaymentTerms = node.InnerText;
111             }
112             xnl = xmldoc.GetElementsByTagName("SHIP_VIA");
113             foreach (XmlNode node in xnl)
114             {
115                 co.ShipVia = node.InnerText;
116             }
117             xnl = xmldoc.GetElementsByTagName("SHIPPING_TERMS");
118             foreach (XmlNode node in xnl)
119             {
120                 co.ShippingTerms = node.InnerText;
121             }
122             xnl = xmldoc.GetElementsByTagName("BILL_TO_CONTACT_NAME");
123             foreach (XmlNode node in xnl)
124             {
125                 co.BillToContactName = node.InnerText;
126             }
127             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_1");
128             foreach (XmlNode node in xnl)
129             {
130                 co.BillToAddressLine1 = node.InnerText;
131             }
132             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_2");
133             foreach (XmlNode node in xnl)
134             {
135                 co.BillToAddressLine2 = node.InnerText;
136             }
137             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_3");
138             foreach (XmlNode node in xnl)
139             {
140                 co.BillToAddressLine3 = node.InnerText;
141             }
142             xnl = xmldoc.GetElementsByTagName("BILL_TO_CITY");
143             foreach (XmlNode node in xnl)
144             {
145                 co.BillToCity = node.InnerText;
146             }
147             xnl = xmldoc.GetElementsByTagName("BILL_TO_STATE");
148             foreach (XmlNode node in xnl)
149             {
150                 co.BillToState = node.InnerText;
151             }
152             xnl = xmldoc.GetElementsByTagName("BILL_TO_COUNTRY");
153             foreach (XmlNode node in xnl)
154             {
155                 co.BillToCountry = node.InnerText;
156             }
157             xnl = xmldoc.GetElementsByTagName("BILL_TO_POSTAL_CODE");
158             foreach (XmlNode node in xnl)
159             {
160                 co.BillToPostalCode = node.InnerText;
161             }
162             xnl = xmldoc.GetElementsByTagName("BILL_TO_PHONE_NUMBER");
163             foreach (XmlNode node in xnl)
164             {
165                 co.BillToPhoneNumber = node.InnerText;
166                 if (co.BillToPhoneNumber.Length == 0) { co.BillToPhoneNumber = "NA"; };
167             }
168             xnl = xmldoc.GetElementsByTagName("BILL_TO_FAX_NUMBER");
169             foreach (XmlNode node in xnl)
170             {
171                 co.BillToFaxNumber = node.InnerText;
172             }
173             xnl = xmldoc.GetElementsByTagName("BILL_TO_EMAIL_ADDRESS");
174             foreach (XmlNode node in xnl)
175             {
176                 co.BillToEmailAddress = node.InnerText;
177             }
178             xnl = xmldoc.GetElementsByTagName("BILL_TO_REF_NUM");
179             foreach (XmlNode node in xnl)
180             {
181                 co.BillToRefNum = node.InnerText;
182             }
183             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CONTACT_NAME");
184             foreach (XmlNode node in xnl)
185             {
186                 co.ShipToContactName = node.InnerText;
187             }
188             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_1");
189             foreach (XmlNode node in xnl)
190             {
191                 co.ShipToAddressLine1 = node.InnerText;
192             }
193             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_2");
194             foreach (XmlNode node in xnl)
195             {
196                 co.ShipToAddressLine2 = node.InnerText;
197             }
198             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_3");
199             foreach (XmlNode node in xnl)
200             {
201                 co.ShipToAddressLine3 = node.InnerText;
202             }
203             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CITY");
204             foreach (XmlNode node in xnl)
205             {
206                 co.ShipToCity = node.InnerText;
207             }
208             xnl = xmldoc.GetElementsByTagName("SHIP_TO_STATE");
209             foreach (XmlNode node in xnl)
210             {
211                 co.ShipToState = node.InnerText;
212             }
213             xnl = xmldoc.GetElementsByTagName("SHIP_TO_COUNTRY");
214             foreach (XmlNode node in xnl)
215             {
216                 co.ShipToCountry = node.InnerText;
217             }
218             xnl = xmldoc.GetElementsByTagName("SHIP_TO_POSTAL_CODE");
219             foreach (XmlNode node in xnl)
220             {
221                 co.ShipToPostalCode = node.InnerText;
222             }
223             xnl = xmldoc.GetElementsByTagName("SHIP_TO_PHONE_NUMBER");
224             foreach (XmlNode node in xnl)
225             {
226                 co.ShipToPhoneNumber = node.InnerText;
227             }
228             xnl = xmldoc.GetElementsByTagName("SHIP_TO_FAX_NUMBER");
229             foreach (XmlNode node in xnl)
230             {
231                 co.ShipToFaxNumber = node.InnerText;
232             }
233             xnl = xmldoc.GetElementsByTagName("SHIP_TO_EMAIL_ADDRESS");
234             foreach (XmlNode node in xnl)
235             {
236                 co.ShipToEmailAddress = node.InnerText;
237             }
238             xnl = xmldoc.GetElementsByTagName("SHIP_TO_REF_NUM");
239             foreach (XmlNode node in xnl)
240             {
241                 co.ShipToRefNum = node.InnerText;
242             }
243             xnl = xmldoc.GetElementsByTagName("PRIORITY_LEVEL");
244             foreach (XmlNode node in xnl)
245             {
246                 co.PriorityLevel = Convert.ToInt16(node.InnerText);
247             }
248             //output CO header to SQL before proceeding to the coitem
249             DatabaseFactory.WriteRecordCO(ref co);
250 
251             //build COITEM records, per line
252             xnl = xmldoc.GetElementsByTagName("Detail");
253             foreach (XmlNode node in xnl)
254             {
255                 coitem.CO_Line = Convert.ToInt16(node.ChildNodes[0].InnerText);
256                 coitem.Serial = node.ChildNodes[2].InnerText;
257                 coitem.Item = node.ChildNodes[3].InnerText;
258                 coitem.Smartpart = node.ChildNodes[4].InnerText;
259                 coitem.Desc = node.ChildNodes[5].InnerText;
260                 coitem.UnitPrice = Convert.ToDecimal(node.ChildNodes[7].InnerText);
261                 coitem.UnitCost = Convert.ToDecimal(node.ChildNodes[8].InnerText);
262                 coitem.Discount = Convert.ToDecimal(node.ChildNodes[9].InnerText);
263                 coitem.QTY = Convert.ToDecimal(node.ChildNodes[10].InnerText);
264                 coitem.PriorityLevel = co.PriorityLevel;
265                 globalOrderLineNum = coitem.CO_Line;
266                 //output coitem record
267                 DatabaseFactory.WriteRecordCOItem(ref coitem);
268 
269                 //*** Everything else here builds on the COITEM ***
270                 
271                 //iterate through Inputs for the line
272                 XmlNodeList xnli = xmldoc.GetElementsByTagName("Input");
273                 foreach (XmlNode nodei in xnli)
274                 {
275                     cfg.CO_Line = coitem.CO_Line;
276                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
277                     cfg.CValue = nodei.ChildNodes[0].InnerText;
278                     cfg.CType = nodei.ChildNodes[1].InnerText;
279                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
280                     cfg.PriorityLevel = co.PriorityLevel;
281                     //output cfg (parmval) record
282                     DatabaseFactory.WriteRecordCfg(ref cfg);
283                 }
284                 //item-master for the line we are working with
285                 int recordSequence = 1;
286                 XmlNodeList xnlim = xmldoc.GetElementsByTagName("ItemMaster");
287                 citem.CO_Line = coitem.CO_Line;
288                 foreach (XmlNode nodeim in xnlim)
289                 {
290                     citem.Sequence = recordSequence;
291                     citem.Smartpart = nodeim.ChildNodes[0].InnerText;
292                     citem.Item = nodeim.ChildNodes[1].InnerText;
293                     citem.Desc = nodeim.ChildNodes[3].InnerText;
294                     string cost = nodeim.ChildNodes[4].InnerText;
295                     string price = nodeim.ChildNodes[5].InnerText;
296                     string sell = nodeim.ChildNodes[6].InnerText;
297                     string weight = nodeim.ChildNodes[9].InnerText;
298                     citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
299                     citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
300                     citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
301                     citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
302                     citem.UnitOfMeasure = nodeim.ChildNodes[26].InnerText;
303                     citem.PriorityLevel = Convert.ToInt16(nodeim.ChildNodes[38].InnerText);
304                     recordSequence += 1;
305                     //output item-master record
306                     DatabaseFactory.WriteRecordCItem(ref citem);
307                 }
308                 //BOM records
309                 recordSequence = 0;
310                 XmlNodeList xnlb = xmldoc.GetElementsByTagName("Bom");
311                 bom.CO_Line = coitem.CO_Line;
312                 foreach (XmlNode nodeib in xnlb)
313                 {
314                     recordSequence = 1;
315                     bom.Sequence = recordSequence;
316                     var parent = nodeib.SelectSingleNode("..");
317                     string parentID = parent.ChildNodes[0].InnerText;
318                     bom.Parent = parentID == "1" ? null : parentID;
319                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
320                     bom.Item = nodeib.ChildNodes[1].InnerText;
321                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
322                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
323                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
324                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
325                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
326                     bom.PriorityLevel = co.PriorityLevel;
327                     //output BOM record
328                     DatabaseFactory.WriteRecordBOM(ref bom);
329                 }
330                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
331                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
332                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
333             }
334         }
335     }
336 }
