395                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
396                     SendMail.MailMessage(Triggers.logEvent, "Config-Type Warning");
397                     continue;           //we cannot process a type K; notify, ignore this line and continue with the next detail node if any exist
398                 }
399 
400                 //output coitem record
401                 DatabaseFactory.WriteRecordCOItem(ref coitem);
402 
403                 //*** Everything else here builds on the COITEM ***
404                 var detailParent = node.SelectSingleNode(".");     //ensure we traverse ONLY children of this node (Detail) as the new parent (root) element
405                 XmlDocument detailDoc = new XmlDocument();
406                 detailDoc.LoadXml(detailParent.OuterXml);
407 
408                 //iterate through Inputs for the line (none of these have static variable names or attributes)
409                 XmlNodeList xnli = detailDoc.GetElementsByTagName("Input");
410                 foreach (XmlNode nodei in xnli)
411                 {
412                     cfg.CO_Line = coitem.CO_Line;
413                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
414                     cfg.CValue = nodei.ChildNodes[0].InnerText;
415                     cfg.CType = nodei.ChildNodes[1].InnerText;
416                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
417                     cfg.PriorityLevel = co.PriorityLevel;
418                     //output cfg (parmval) record
419                     DatabaseFactory.WriteRecordCfg(ref cfg);
420                 }
421 
422                 //Look for SHIP_VIA in INPUTS 
423                 co.ShipVia = " ";
424                 XmlNodeList xnlisv = xmldoc.GetElementsByTagName("Input");
425                 foreach (XmlNode nodeisv in xnlisv)
426                 {
427                     if (nodeisv.ChildNodes[2].InnerText == "SHIP_VIA") { co.ShipVia = nodeisv.ChildNodes[0].Attributes["name"].Value; }
428                 }
429                 
430                 //item-master for the line we are working with
431                 int recordSequence = 1;
432                 XmlNodeList xnlim = detailDoc.GetElementsByTagName("ItemMaster");
433                 citem.CO_Line = coitem.CO_Line;
434                 string cost = "";
435                 string price = "";
436                 string sell = "";
437                 string weight = "";
438                 citem.IM_VAR1 = "";
439                 citem.IM_VAR2 = "";
440                 citem.IM_VAR3 = "";
441                 citem.IM_VAR4 = "";
442                 citem.IM_VAR5 = "";
443                 //Build nodelist of ItemMaster nodes and a second nodelist within of its childnodes so we can reference those elements by name instead of index
444                 foreach (XmlNode nodeim in xnlim)
445                 {
446                     foreach (XmlNode childIM in nodeim.ChildNodes)
447                     {
448                         switch (childIM.Name)
449                         {
450                             case "SMARTPART_NUM":
451                                 citem.Smartpart = childIM.InnerText;
452                                 break;
453                             case "ITEM_NUM":
454                                 citem.Item = childIM.InnerText;
455                                 break;
456                             case "DESCRIPTION":
457                                 citem.Desc = childIM.InnerText;
458                                 break;
459                             case "COST":
460                                 cost = childIM.InnerText;
461                                 break;
462                             case "PRICE":
463                                 price = childIM.InnerText;
464                                 break;
465                             case "SELL_PRICE":
466                                 sell = childIM.InnerText;
467                                 break;
468                             case "WEIGHT":
469                                 weight = childIM.InnerText;
470                                 break;
471                             case "UOM":
472                                 citem.UnitOfMeasure = childIM.InnerText;
473                                 break;
474                             case "PRIORITY_LEVEL":
475                                 citem.PriorityLevel = Convert.ToInt16(childIM.InnerText);
476                                 break;
477                             case "VAR_1":
478                                 citem.IM_VAR1 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
479                                 break;
480                             case "VAR_2":
481                                 citem.IM_VAR2 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
482                                 break;
483                             case "VAR_3":
484                                 citem.IM_VAR3 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
485                                 break;
486                             case "VAR_4":
487                                 citem.IM_VAR4 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
488                                 break;
489                             case "VAR_5":
490                                 citem.IM_VAR5 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
491                                 break;
492                             default:
493                                 //do nothing
494                                 break;
495                         }
496                         citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
497                         citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
498                         citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
499                         citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
500                         citem.Sequence = recordSequence;
501                     }
502                     recordSequence += 1;
503                     DatabaseFactory.WriteRecordCItem(ref citem);
504                     cost = "";
505                     price = "";
506                     sell = "";
507                     weight = "";
508                     citem.IM_VAR1 = "";
509                     citem.IM_VAR2 = "";
510                     citem.IM_VAR3 = "";
511                     citem.IM_VAR4 = "";
512                     citem.IM_VAR5 = "";
513                 }
514 
515                 //BOM records (Must load in this manner, as none of these have static variable names or attributes)
516                 recordSequence = 0;
517                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
518                 bom.CO_Line = coitem.CO_Line;
519                 foreach (XmlNode nodeib in xnlb)
520                 {
521                     recordSequence += 1;
522                     bom.Sequence = recordSequence;
523                     bom.RecordSequence = recordSequence;
524                     var parent = nodeib.SelectSingleNode("..");
525                     string parentID = parent.ChildNodes[0].InnerText;
526                     bom.Parent = parentID == "1" ? null : parentID;
527                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
528                     bom.Item = nodeib.ChildNodes[1].InnerText;
529                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
530                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
531                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
532                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
533                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
534                     
535                     //Search the ItemMaster XML records for the matching smartpart_num and retrieve its priority level for this BOM record
536                     bool foundchild = false;
537                     bool imdone = false;
538                     foreach (XmlNode nodeIMPL in xnlim)
539                     {
540                         if (imdone == true)
541                         {
542                             break;
543                         }
544                         foreach (XmlNode childIMPL in nodeIMPL.ChildNodes)
545                         {
546                             if (imdone == true)
547                             {
548                                 break;
549                             }
550                             if (childIMPL.InnerText == bom.Smartpart)
551                             {
552                                 foundchild = true;
553                             }
554                             if (foundchild == true)
555                             {
556                                 if (childIMPL.Name == "PRIORITY_LEVEL")
557                                 {
558                                     bom.PriorityLevel = Convert.ToInt16(childIMPL.InnerText);
559                                     imdone = true;
560                                 }
561                             }
562                         }
563                     }
564                     //output BOM record
565                     DatabaseFactory.WriteRecordBOM(ref bom);
566                 }
567                 //Routing - Output only if the Routing tag contains a nodelist count greater than 0
568                 Triggers.logEvent = "STARTING ROUTING LOGIC";
569                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
570                 int routebomSeq = 0;
571                 bool UseRouteBOM = false;
572                 route.CO_Num = coitem.CO_Num;
573                 route.CO_Line = coitem.CO_Line;
574                 XmlNodeList xnlr = detailDoc.GetElementsByTagName("Routing");
575                 if (xnlr.Count > 0)
576                 {
577                     UseRouteBOM = true;
578                     foreach (XmlNode nodecr in xnlr)
579                     {
580                         XmlNode nodecrtg = nodecr.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
581                         route.SmartpartNum = nodecrtg.ChildNodes[0].InnerText;
582                         nodecrtg = nodecr.SelectSingleNode("c1:ITEM_NUM", nsmgr);
583                         route.ItemNum = nodecrtg.ChildNodes[0].InnerText;
584                         nodecrtg = nodecr.SelectSingleNode("c1:BOM_ID", nsmgr);
585                         route.BOM_ID = nodecrtg.ChildNodes[0].InnerText;
586 
587                         //isolate OPERATION elements from Routing and process
588                         XmlDocument routeOP = new XmlDocument();
589                         routeOP.LoadXml(nodecr.OuterXml);
590                         XmlNodeList xnlOperation = routeOP.GetElementsByTagName("Operation");
591                         foreach (XmlNode nodeol in xnlOperation)
592                         {
593                             XmlNode operationParentTL = nodeol.SelectSingleNode(".");             //current operation becomes our new parent (.. to .)
594                             XmlDocument operationDocumentTL = new XmlDocument();
595                             operationDocumentTL.LoadXml(operationParentTL.OuterXml);
596                             XmlNodeList xnlOP = operationDocumentTL.GetElementsByTagName("OperationParam");
597                             foreach (XmlNode nodeParamChild in xnlOP)
598                             {
599                                 if (nodeParamChild.ChildNodes[0].InnerText == "LABOR_HRS") { route.Labor_Hours = Convert.ToDouble(nodeParamChild.ChildNodes[2].InnerText); }
600                                 if (nodeParamChild.ChildNodes[0].InnerText == "SETUP_HRS") { route.Setup_Hours = Convert.ToDouble(nodeParamChild.ChildNodes[2].InnerText); }
601                                 if (nodeParamChild.ChildNodes[0].InnerText == "WC") { route.WC = nodeParamChild.ChildNodes[2].InnerText; }
602                                 if (nodeParamChild.ChildNodes[0].InnerText == "NOTES") { route.Notes = nodeParamChild.ChildNodes[2].InnerText; }
603                                 if (nodeParamChild.ChildNodes[0].InnerText == "MACH_NAME") { route.Machine_Name = nodeParamChild.ChildNodes[2].InnerText; }
604                             }
605 
606                             XmlNode nodeOpItem = nodeol.SelectSingleNode("c1:OPER_NUM", nsmgr);
607                             route.OPERATION = Convert.ToInt16(nodeOpItem.ChildNodes[0].InnerText);
608                             nodeOpItem = nodeol.SelectSingleNode("c1:DESCRIPTION", nsmgr);
609                             route.Description = nodeOpItem.ChildNodes[0].InnerText;
610 
611                             //set current operation as new parent and look ONLY for its OperationInput tags
612                             var operationParent = nodeol.SelectSingleNode(".");     //ensure we traverse ONLY descendants of this node (Operation) as the new parent 
613                             
614                             XmlDocument operationDoc = new XmlDocument();
615                             operationDoc.LoadXml(operationParent.OuterXml);
616                             XmlNodeList xnlOperationInputs = operationDoc.GetElementsByTagName("OperationInput");
617 
618                             foreach (XmlNode nodeOpInput in xnlOperationInputs)
619                             {
620                                 XmlNode nodeoin = nodeOpInput.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
621                                 route.MatlSmartpartNum = nodeOpInput.ChildNodes[0].InnerText;
622                                 route.MatlItemNum = nodeOpInput.ChildNodes[1].InnerText;
623                                 route.MatlQty = Convert.ToDecimal(nodeOpInput.ChildNodes[2].InnerText);
624                                 //write to gr_cfgroutebom
625                                 routebomSeq += 1;
626                                 route.Seq = routebomSeq;
627                                 DatabaseFactory.WriteRecordCfgRoute(ref route);
628                             }
629                         }
630                     }
631                 }
632 
633                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
634                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
635                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
636 
637                 //If we used routeBOM, dump the old-process BOM records
638                 //if (UseRouteBOM) { DatabaseFactory.DeleteBOM(co.CORefNum); }          //this may be reinstated in the future
639             }
640             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
641         }
642         public static string LoadFromXML(XmlDocument xmldoc, string element, XmlNamespaceManager nsmgr)
643         {
644             string returnValue = "";
645 
646             try
647             {
648                 XmlNode xnode = xmldoc.SelectSingleNode(element, nsmgr);
649                 returnValue = string.IsNullOrEmpty(xnode.InnerText) ? " " : xnode.InnerText;
650             }
651             catch (Exception exLoad)
652             {
653                 Triggers.logEvent = "Attempt to access XML tag <" + element + "> failed:  " + exLoad.Message;
654                 string auditMessage = "Attempt to access XML tag (" + element + ") failed:  " + exLoad.Message;
655                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
656                 returnValue = " ";
657 
658                 Audit.SetTruncate("MAPPING", 2, 1, StagingUtilities.globalOrderNum, StagingUtilities.globalOrderLineNum, auditMessage);
659 
660             }
661             
662             return returnValue;
663         }
