520                                 break;
521                             default:
522                                 //do nothing
523                                 break;
524                         }
525                         citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
526                         citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
