529                         citem.Sequence = recordSequence;
530                     }
531                     recordSequence += 1;
532                     DatabaseFactory.WriteRecordCItem(ref citem);
533                     cost = "";
534                     price = "";
535                     sell = "";
536                     weight = "";
537                     citem.IM_VAR1 = "";
538                     citem.IM_VAR2 = "";
539                     citem.IM_VAR3 = "";
540                     citem.IM_VAR4 = "";
541                     citem.IM_VAR5 = "";
542                 }
