544                 //BOM records
545                 recordSequence = 0;
546                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
547                 bom.CO_Line = coitem.CO_Line;
548                 foreach (XmlNode nodeib in xnlb)
549                 {
550                     recordSequence += 1;
551                     bom.Sequence = recordSequence;
552                     bom.RecordSequence = recordSequence;
553                     var parent = nodeib.SelectSingleNode("..");
554                     string parentID = parent.ChildNodes[0].InnerText;
555                     bom.Parent = parentID == "1" ? null : parentID;
556                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
557                     bom.Item = nodeib.ChildNodes[1].InnerText;
558                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
559                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
560                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
561                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
562                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
563 
564                     //Search the ItemMaster XML records for the matching smartpart_num and retrieve its priority level for this BOM record
565                     bool foundchild = false;
566                     bool imdone = false;
567                     foreach (XmlNode nodeIMPL in xnlim)
568                     {
569                         if (imdone == true)
570                         {
571                             break;
572                         }
573                         foreach (XmlNode childIMPL in nodeIMPL.ChildNodes)
574                         {
575                             if (imdone == true)
576                             {
577                                 break;
578                             }
579                             if (childIMPL.InnerText == bom.Smartpart)
580                             {
581                                 foundchild = true;
582                             }
583                             if (foundchild == true)
584                             {
585                                 if (childIMPL.Name == "PRIORITY_LEVEL")
586                                 {
587                                     bom.PriorityLevel = Convert.ToInt16(childIMPL.InnerText);
588                                     imdone = true;
589                                 }
590                             }
591                         }
592                     }
593                     //output BOM record
594                     DatabaseFactory.WriteRecordBOM(ref bom);
595                 }
596 
597                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
598                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
599                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
600             }
601             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
602         }
603     }
604 }
