14         public static void MapXMLToSQL(XmlDocument xmldoc)
15         {
16             zCfgCO co = new zCfgCO();
17             zCfgCOitem coitem = new zCfgCOitem();
18             zCfgItem citem = new zCfgItem();
19             zCfgParmVal cfg = new zCfgParmVal();
20             zCfgBOM bom = new zCfgBOM();
21 
22             Audit.resetmE = true;       //reset the mE array in case we have any mapping errors to report for this cycle
23             
24             //*** Determine what site we are working with and re-set the connection string accordingly, else default to NOVB
25             XmlNodeList xnlsite = xmldoc.GetElementsByTagName("Input");
26             foreach (XmlNode node in xnlsite)
27             {
28                 switch (node.ChildNodes[2].InnerText == "ORDER SITE")
29                 {
30                     case true:
31                         string rplConnectionString = DatabaseFactory.connectionString;
32                         int csPos = rplConnectionString.IndexOf("NOVB");
33                         DatabaseFactory.connectionString = rplConnectionString.Substring(0, csPos) + node.ChildNodes[0].Attributes["name"].Value + rplConnectionString.Substring(csPos + 4, rplConnectionString.Length - (csPos + 4));
34                         Triggers.logEvent = "Connection String: " + DatabaseFactory.connectionString;
35                         System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
36                         break;
37                     default:
38                         //continue with NOVB default
39                         break;
40                 }
41             }
42 
43             //Retrieve due date
44             co.DueDate = DateTime.Now;
45             coitem.DueDate = DateTime.Now;
46             XmlNodeList xnlduedate = xmldoc.GetElementsByTagName("Input");
47             foreach (XmlNode node in xnlduedate)
48             {
49                 switch (node.ChildNodes[2].InnerText == "DUE DATE")
50                 {
51                     case true:
52                         co.DueDate = Convert.ToDateTime(node.ChildNodes[0].Attributes["name"].Value);
53                         coitem.DueDate = Convert.ToDateTime(node.ChildNodes[0].Attributes["name"].Value);
54                         break;
55                     default:
56                         //do nothing
57                         break;
58                 }
59             }
60             
61             //Pre-staging activities
62             XmlNodeList xnl = xmldoc.GetElementsByTagName("ORDER_NUM");
63             foreach (XmlNode node in xnl)
64             {
65                 co.CO_Num = node.InnerText;
66                 coitem.CO_Num = node.InnerText;
67                 cfg.CO_Num = node.InnerText;
68                 citem.CO_Num = node.InnerText;
69                 bom.CO_Num = node.InnerText;
70                 globalOrderNum = co.CO_Num;
71                 globalOrderLineNum = 0;
72             }
73             
74             switch (co.CO_Num == null || co.CO_Num == "")
75             {
76                 case true:
77                     Triggers.logEvent = "ORDER NUMBER NOT FOUND: " + co.CO_Num;
78                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
79                     return;
80                 default:
81                     //Remove any pre-existing records in SQL for this order
82                     DatabaseFactory.CleanupOrder(co.CO_Num);
83                     break;
84             }
85             Triggers.logEvent = "MAPPING XML TO STAGING TABLES";
86             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
87 
88             //build CO header
89             xnl = xmldoc.GetElementsByTagName("ID");
90             foreach (XmlNode node in xnl)
91             {
92                 co.Identifier = node.InnerText;
93                 break;          //we want first occurrence, ONLY
94             }
95             xnl = xmldoc.GetElementsByTagName("ORDER_REF_NUM");
96             foreach (XmlNode node in xnl)
97             {
98                 co.CORefNum = node.InnerText;
99             }
100             xnl = xmldoc.GetElementsByTagName("CUST_NAME");
101             foreach (XmlNode node in xnl)
102             {
103                 co.CustName = node.InnerText;
104             }
105             xnl = xmldoc.GetElementsByTagName("CUST_REF_NUM");
106             foreach (XmlNode node in xnl)
107             {
108                 co.CustRefNum = node.InnerText;
109             }
110             xnl = xmldoc.GetElementsByTagName("ACCOUNT_NUM");
111             foreach (XmlNode node in xnl)
112             {
113                 co.AccountNum = node.InnerText;
114             }
115             xnl = xmldoc.GetElementsByTagName("ERP_REFERENCE_NUM");
116             foreach (XmlNode node in xnl)
117             {
118                 co.ErpReferenceNum = node.InnerText;
119             }
120             xnl = xmldoc.GetElementsByTagName("PROJECT");
121             foreach (XmlNode node in xnl)
122             {
123                 co.Project = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
124             }
125             xnl = xmldoc.GetElementsByTagName("PAYMENT_TERMS");
126             foreach (XmlNode node in xnl)
127             {
128                 co.PaymentTerms = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
129             }
130             xnl = xmldoc.GetElementsByTagName("SHIP_VIA");
131             foreach (XmlNode node in xnl)
132             {
133                 co.ShipVia = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
134             }
135             xnl = xmldoc.GetElementsByTagName("SHIPPING_TERMS");
136             foreach (XmlNode node in xnl)
137             {
138                 co.ShippingTerms = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
139             }
140             xnl = xmldoc.GetElementsByTagName("BILL_TO_CONTACT_NAME");
141             foreach (XmlNode node in xnl)
142             {
143                 co.BillToContactName = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
144             }
145             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_1");
146             foreach (XmlNode node in xnl)
147             {
148                 co.BillToAddressLine1 = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
149             }
150             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_2");
151             foreach (XmlNode node in xnl)
152             {
153                 co.BillToAddressLine2 = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
154             }
155             xnl = xmldoc.GetElementsByTagName("BILL_TO_ADDRESS_LINE_3");
156             foreach (XmlNode node in xnl)
157             {
158                 co.BillToAddressLine3 = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
159             }
160             xnl = xmldoc.GetElementsByTagName("BILL_TO_CITY");
161             foreach (XmlNode node in xnl)
162             {
163                 co.BillToCity = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
164             }
165             xnl = xmldoc.GetElementsByTagName("BILL_TO_STATE");
166             foreach (XmlNode node in xnl)
167             {
168                 co.BillToState = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
169             }
170             xnl = xmldoc.GetElementsByTagName("BILL_TO_COUNTRY");
171             foreach (XmlNode node in xnl)
172             {
173                 co.BillToCountry = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
174             }
175             xnl = xmldoc.GetElementsByTagName("BILL_TO_POSTAL_CODE");
176             foreach (XmlNode node in xnl)
177             {
178                 co.BillToPostalCode = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
179             }
180             xnl = xmldoc.GetElementsByTagName("BILL_TO_PHONE_NUMBER");
181             foreach (XmlNode node in xnl)
182             {
183                 co.BillToPhoneNumber = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
184             }
185             xnl = xmldoc.GetElementsByTagName("BILL_TO_FAX_NUMBER");
186             foreach (XmlNode node in xnl)
187             {
188                 co.BillToFaxNumber = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
189             }
190             xnl = xmldoc.GetElementsByTagName("BILL_TO_EMAIL_ADDRESS");
191             foreach (XmlNode node in xnl)
192             {
193                 co.BillToEmailAddress = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
194             }
195             xnl = xmldoc.GetElementsByTagName("BILL_TO_REF_NUM");
196             foreach (XmlNode node in xnl)
197             {
198                 co.BillToRefNum = node.InnerText;
199             }
200             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CONTACT_NAME");
201             foreach (XmlNode node in xnl)
202             {
203                 co.ShipToContactName = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
204             }
205             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_1");
206             foreach (XmlNode node in xnl)
207             {
208                 co.ShipToAddressLine1 = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
209             }
210             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_2");
211             foreach (XmlNode node in xnl)
212             {
213                 co.ShipToAddressLine2 = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
214             }
215             xnl = xmldoc.GetElementsByTagName("SHIP_TO_ADDRESS_LINE_3");
216             foreach (XmlNode node in xnl)
217             {
218                 co.ShipToAddressLine3 = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
219             }
220             xnl = xmldoc.GetElementsByTagName("SHIP_TO_CITY");
221             foreach (XmlNode node in xnl)
222             {
223                 co.ShipToCity = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
224             }
225             xnl = xmldoc.GetElementsByTagName("SHIP_TO_STATE");
226             foreach (XmlNode node in xnl)
227             {
228                 co.ShipToState = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
229             }
230             xnl = xmldoc.GetElementsByTagName("SHIP_TO_COUNTRY");
231             foreach (XmlNode node in xnl)
232             {
233                 co.ShipToCountry = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
234             }
235             xnl = xmldoc.GetElementsByTagName("SHIP_TO_POSTAL_CODE");
236             foreach (XmlNode node in xnl)
237             {
238                 co.ShipToPostalCode = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
239             }
240             xnl = xmldoc.GetElementsByTagName("SHIP_TO_PHONE_NUMBER");
241             foreach (XmlNode node in xnl)
242             {
243                 co.ShipToPhoneNumber = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
244             }
245             xnl = xmldoc.GetElementsByTagName("SHIP_TO_FAX_NUMBER");
246             foreach (XmlNode node in xnl)
247             {
248                 co.ShipToFaxNumber = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
249             }
250             xnl = xmldoc.GetElementsByTagName("SHIP_TO_EMAIL_ADDRESS");
251             foreach (XmlNode node in xnl)
252             {
253                 co.ShipToEmailAddress = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
254             }
255             xnl = xmldoc.GetElementsByTagName("SHIP_TO_REF_NUM");
256             foreach (XmlNode node in xnl)
257             {
258                 co.ShipToRefNum = node.InnerText;
259             }
260             xnl = xmldoc.GetElementsByTagName("PRIORITY_LEVEL");
261             foreach (XmlNode node in xnl)
262             {
263                 co.PriorityLevel = Convert.ToInt16(node.InnerText);
264             }
265             xnl = xmldoc.GetElementsByTagName("SERIAL_NUMBER");
266             foreach (XmlNode node in xnl)
267             {
268                 co.QuoteNbr = node.InnerText.Length == 0 ? "N/A" : node.InnerText;
269             }
270             xnl = xmldoc.GetElementsByTagName("CREATED_BY_USER_ID");
271             foreach (XmlNode node in xnl)
272             {
273                 co.WebUserName = DatabaseFactory.UserName(node.InnerText);
274             }
275 
276             co.WebOrderDate = System.DateTime.Now;
277             
278             //Look for PURCHASE ORDER in INPUTS, load into CO and COITEM
279             XmlNodeList xnlPO = xmldoc.GetElementsByTagName("Input");
280             foreach (XmlNode nodePO in xnlPO)
281             {
282                 if (nodePO.ChildNodes[2].InnerText == "PURCHASE ORDER")
283                 {
284                     co.CustPO = nodePO.ChildNodes[0].Attributes["name"].Value;
285                     coitem.CustPO = nodePO.ChildNodes[0].Attributes["name"].Value;
286                 }
287             }
288 
289             //Look for FREIGHT ACCOUNT# (IF COLLECT) in INPUTS, load into CO
290             XmlNodeList xnlCO = xmldoc.GetElementsByTagName("Input");
291             foreach (XmlNode nodeCO in xnlCO)
292             {
293                 if (nodeCO.ChildNodes[2].InnerText == "FREIGHT ACCOUNT# (IF COLLECT)")
294                 {
295                     switch(nodeCO.ChildNodes[0].Attributes["name"].Value.Length > 0)
296                     {
297                         case true:
298                             co.FreightAcct = nodeCO.ChildNodes[0].Attributes["name"].Value;
299                             break;
300                         default:
301                             co.FreightAcct = "N/A";
302                             break;
303                     }
304                 }
305             }
306 
307             //Look for FREIGHT TERMS in INPUTS, load into CO
308             XmlNodeList xnlCOFT = xmldoc.GetElementsByTagName("Input");
309             foreach (XmlNode nodeCOFT in xnlCOFT)
310             {
311                 if (nodeCOFT.ChildNodes[2].InnerText == "FREIGHT TERMS")
312                 {
313                     switch (nodeCOFT.ChildNodes[0].Attributes["name"].Value.Length > 0)
314                     {
315                         case true:
316                             co.FreightTerms = nodeCOFT.ChildNodes[0].Attributes["name"].Value;
317                             break;
318                         default:
319                             co.FreightTerms = "N/A";
320                             break;
321                     }
322                 }
323             }
324 
325             //Look for REQUEST DATE in INPUTS, load into CO
326             XmlNodeList xnlCORD = xmldoc.GetElementsByTagName("Input");
327             foreach (XmlNode nodeCORD in xnlCORD)
328             {
329                 if (nodeCORD.ChildNodes[2].InnerText == "REQUEST DATE")
330                 {
331                     switch (nodeCORD.ChildNodes[0].Attributes["name"].Value.Length > 0)
332                     {
333                         case true:
334                             co.RequestDate = Convert.ToDateTime(nodeCORD.ChildNodes[0].Attributes["name"].Value);
335                             break;
336                         default:
337                             co.RequestDate = System.DateTime.Now;       //default to today's date if no request date exists in the XML document
338                             break;
339                     }
340                 }
341             }
342 
343             //build COITEM records, per line
344             xnl = xmldoc.GetElementsByTagName("Detail");
345             foreach (XmlNode node in xnl)
346             {
347                 coitem.CO_Line = Convert.ToInt16(node.ChildNodes[0].InnerText);
348                 coitem.Serial = node.ChildNodes[2].InnerText;
349                 coitem.Item = node.ChildNodes[3].InnerText;
350                 coitem.Smartpart = node.ChildNodes[4].InnerText;
351                 coitem.Desc = node.ChildNodes[5].InnerText;
352                 coitem.ConfigType = node.ChildNodes[6].InnerText;
353                 coitem.UnitPrice = Convert.ToDecimal(node.ChildNodes[7].InnerText);
354                 coitem.UnitCost = Convert.ToDecimal(node.ChildNodes[8].InnerText);
355                 coitem.Discount = Convert.ToDecimal(node.ChildNodes[9].InnerText);
356                 coitem.QTY = Convert.ToDecimal(node.ChildNodes[10].InnerText);
357                 coitem.PriorityLevel = co.PriorityLevel;
358                 globalOrderLineNum = coitem.CO_Line;
359                 //output coitem record
360                 DatabaseFactory.WriteRecordCOItem(ref coitem);
361                 
362                 //*** Everything else here builds on the COITEM ***
363                 var detailParent = node.SelectSingleNode(".");     //ensure we traverse ONLY children of this node (Detail) as the new parent (root) element
364                 XmlDocument detailDoc = new XmlDocument();
365                 detailDoc.LoadXml(detailParent.OuterXml);
366 
367                 //iterate through Inputs for the line
368                 XmlNodeList xnli = detailDoc.GetElementsByTagName("Input");
369                 foreach (XmlNode nodei in xnli)
370                 {
371                     cfg.CO_Line = coitem.CO_Line;
372                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
373                     cfg.CValue = nodei.ChildNodes[0].InnerText;
374                     cfg.CType = nodei.ChildNodes[1].InnerText;
375                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
376                     cfg.PriorityLevel = co.PriorityLevel;
377                     //output cfg (parmval) record
378                     DatabaseFactory.WriteRecordCfg(ref cfg);
379                 }
380 
381                 //Look for SHIP_VIA in INPUTS (cannot use IIF because there are ship_via tags after this one and value will be lost)
382                 XmlNodeList xnlisv = xmldoc.GetElementsByTagName("Input");
383                 foreach (XmlNode nodeisv in xnlisv)
384                 {
385                     if (nodeisv.ChildNodes[2].InnerText == "SHIP_VIA") { co.ShipVia = nodeisv.ChildNodes[0].Attributes["name"].Value; }
386                 }
387                 
388                 //If dropship has value, override the order-header shipping with this information instead
389                 //UPDATE 6/16/2017:  Preserve the selected ship-to from C1 and if dropship has value, load up the dropship fields
390                 XmlNodeList xnlds = xmldoc.GetElementsByTagName("Input");
391                 foreach (XmlNode nodeds in xnlds)
392                 {
393                     if (nodeds.ChildNodes[2].InnerText.Length >= 9 && nodeds.ChildNodes[2].InnerText.Substring(0, 9) == "DROP SHIP")
394                     {
395                         switch (nodeds.ChildNodes[2].InnerText)
396                         {
397                             case "DROP SHIP NAME":
398                                 co.DropShipName = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
399                                 break;
400                             case "DROP SHIP ADDRESS 1":
401                                 co.DropShipAddress1 = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
402                                 break;
403                             case "DROP SHIP ADDRESS 2":
404                                 co.DropShipAddress2 = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
405                                 break;
406                             case "DROP SHIP ADDRESS 3":
407                                 co.DropShipAddress3 = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
408                                 break;
409                             case "DROP SHIP ADDRESS 4":
410                                 co.DropShipAddress4 = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
411                                 break;
412                             case "DROP SHIP CITY":
413                                 co.DropShipCity = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
414                                 break;
415                             case "DROP SHIP STATE":
416                                 co.DropShipState = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
417                                 break;
418                             case "DROP SHIP ZIP CODE":
419                                 co.DropShipZip = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
420                                 break;
421                             case "DROP SHIP CONTACT":
422                                 co.DropShipContact = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
423                                 break;
424                             case "DROP SHIP PHONE":
425                                 co.DropShipPhone = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
426                                 break;
427                             case "DROP SHIP COUNTRY":
428                                 co.DropShipCountry = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
429                                 break;
430                             case "DROP SHIP EMAIL":
431                                 co.DropShipEmail = nodeds.ChildNodes[0].Attributes["name"].Value.Length == 0 ? "N/A" : nodeds.ChildNodes[0].Attributes["name"].Value;
432                                 break;
433                             default:
434                                 //do nothing
435                                 break;
436                         }
437                     }
438                 }
439                 
440                 //item-master for the line we are working with
441                 int recordSequence = 1;
442                 XmlNodeList xnlim = detailDoc.GetElementsByTagName("ItemMaster");
443                 citem.CO_Line = coitem.CO_Line;
444                 foreach (XmlNode nodeim in xnlim)
445                 {
446                     citem.Sequence = recordSequence;
447                     citem.Smartpart = nodeim.ChildNodes[0].InnerText;
448                     citem.Item = nodeim.ChildNodes[1].InnerText;
449                     citem.Desc = nodeim.ChildNodes[3].InnerText;
450                     string cost = nodeim.ChildNodes[4].InnerText;
451                     string price = nodeim.ChildNodes[5].InnerText;
452                     string sell = nodeim.ChildNodes[6].InnerText;
453                     string weight = nodeim.ChildNodes[9].InnerText;
454                     citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
455                     citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
456                     citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
457                     citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
458                     citem.UnitOfMeasure = nodeim.ChildNodes[26].InnerText;
459                     citem.PriorityLevel = Convert.ToInt16(nodeim.ChildNodes[38].InnerText);
460                     recordSequence += 1;
461                     //output item-master record if priority is <= 2    (added 1 == 1 to force record to write regardless of PL, per Grant)
462                     if (citem.PriorityLevel <= 2 || 1 == 1)
463                     {
464                         DatabaseFactory.WriteRecordCItem(ref citem);
465                     }
466                 }
467                 //BOM records
468                 recordSequence = 0;
469                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
470                 bom.CO_Line = coitem.CO_Line;
471                 foreach (XmlNode nodeib in xnlb)
472                 {
473                     recordSequence = 1;
474                     bom.Sequence = recordSequence;
475                     var parent = nodeib.SelectSingleNode("..");
476                     string parentID = parent.ChildNodes[0].InnerText;
477                     bom.Parent = parentID == "1" ? null : parentID;
478                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
479                     bom.Item = nodeib.ChildNodes[1].InnerText;
480                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
481                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
482                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
483                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
484                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
485                     bom.PriorityLevel = co.PriorityLevel;
486                     //output BOM record
487                     DatabaseFactory.WriteRecordBOM(ref bom);
488                 }
489                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
490                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
491                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
492             }
493             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
494         }
495     }
496 }
