466                 nodertv = node.SelectSingleNode("c1:UNIT_COST", nsmgr);
467                 coitem.UnitCost = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
468                 nodertv = node.SelectSingleNode("c1:DISCOUNT_AMT", nsmgr);
469                 coitem.Discount = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
470                 nodertv = node.SelectSingleNode("c1:QUANTITY", nsmgr);
471                 coitem.QTY = Convert.ToDecimal(nodertv.ChildNodes[0].InnerText);
472                 coitem.PriorityLevel = co.PriorityLevel;
473                 globalOrderLineNum = coitem.CO_Line;
474 
475                 coitem.OrderLineNotes = " ";
476                 
477                 //Look for Line Notes, load into COItem
478                 try
479                 {
480                     nodertv = node.SelectSingleNode("c1:Input[@name='LINE_NOTES']", nsmgr);
481                     coitem.OrderLineNotes = nodertv.ChildNodes[0].Attributes["name"].InnerXml.Length == 0 ? " " : nodertv.ChildNodes[0].Attributes["name"].InnerXml;
482                 }
483                 catch (Exception exrtv)
484                 {
485                     Triggers.logEvent = "Line_Note error: " + exrtv.Message;
486                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
487                 }
488 
489                 if (coitem.ConfigType == "K")
490                 {
491                     Triggers.logEvent = "WARNING: Config Type Is: " + coitem.ConfigType + " On C1 Order#: " + co.CO_Num + ". Ignoring line# " + coitem.CO_Line;
492                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
493                     SendMail.MailMessage(Triggers.logEvent, "Config-Type Warning");
494                     continue;           //we cannot process a type K; notify, ignore this line and continue with the next detail node if any exist
495                 }
496 
497                 //output coitem record
498                 DatabaseFactory.WriteRecordCOItem(ref coitem);
499 
500                 //*** Everything else here builds on the COITEM ***
501                 var detailParent = node.SelectSingleNode(".");     //ensure we traverse ONLY children of this node (Detail) as the new parent (root) element
502                 XmlDocument detailDoc = new XmlDocument();
503                 detailDoc.LoadXml(detailParent.OuterXml);
504 
505                 //iterate through Inputs for the line (none of these have static variable names or attributes)
506                 XmlNodeList xnli = detailDoc.GetElementsByTagName("Input");
507                 foreach (XmlNode nodei in xnli)
508                 {
509                     cfg.CO_Line = coitem.CO_Line;
510                     cfg.CName = nodei.ChildNodes[2].InnerText.Replace(" ", "_");
511                     cfg.CValue = nodei.ChildNodes[0].InnerText;
512                     cfg.CType = nodei.ChildNodes[1].InnerText;
513                     cfg.CLabel = nodei.ChildNodes[2].InnerText;
514                     cfg.PriorityLevel = co.PriorityLevel;
515                     //output cfg (parmval) record
516                     DatabaseFactory.WriteRecordCfg(ref cfg);
517                 }
518 
519                 //Look for SHIP_VIA in INPUTS 
520                 co.ShipVia = " ";
521                 XmlNodeList xnlisv = xmldoc.GetElementsByTagName("Input");
522                 foreach (XmlNode nodeisv in xnlisv)
523                 {
524                     if (nodeisv.ChildNodes[2].InnerText == "SHIP_VIA") { co.ShipVia = nodeisv.ChildNodes[0].Attributes["name"].Value; }
525                 }
526                 
527                 //item-master for the line we are working with
528                 int recordSequence = 1;
529                 XmlNodeList xnlim = detailDoc.GetElementsByTagName("ItemMaster");
530                 citem.CO_Line = coitem.CO_Line;
531                 string cost = "";
532                 string price = "";
533                 string sell = "";
534                 string weight = "";
535                 citem.IM_VAR1 = "";
536                 citem.IM_VAR2 = "";
537                 citem.IM_VAR3 = "";
538                 citem.IM_VAR4 = "";
539                 citem.IM_VAR5 = "";
540                 //Build nodelist of ItemMaster nodes and a second nodelist within of its childnodes so we can reference those elements by name instead of index
541                 foreach (XmlNode nodeim in xnlim)
542                 {
543                     foreach (XmlNode childIM in nodeim.ChildNodes)
544                     {
545                         switch (childIM.Name)
546                         {
547                             case "SMARTPART_NUM":
548                                 citem.Smartpart = childIM.InnerText;
549                                 break;
550                             case "ITEM_NUM":
551                                 citem.Item = childIM.InnerText;
552                                 break;
553                             case "DESCRIPTION":
554                                 citem.Desc = childIM.InnerText;
555                                 break;
556                             case "COST":
557                                 cost = childIM.InnerText;
558                                 break;
559                             case "PRICE":
560                                 price = childIM.InnerText;
561                                 break;
562                             case "SELL_PRICE":
563                                 sell = childIM.InnerText;
564                                 break;
565                             case "WEIGHT":
566                                 weight = childIM.InnerText;
567                                 break;
568                             case "UOM":
569                                 citem.UnitOfMeasure = childIM.InnerText;
570                                 break;
571                             case "PRIORITY_LEVEL":
572                                 citem.PriorityLevel = Convert.ToInt16(childIM.InnerText);
573                                 break;
574                             case "VAR_1":
575                                 citem.IM_VAR1 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
576                                 break;
577                             case "VAR_2":
578                                 citem.IM_VAR2 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
579                                 break;
580                             case "VAR_3":
581                                 citem.IM_VAR3 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
582                                 break;
583                             case "VAR_4":
584                                 citem.IM_VAR4 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
585                                 break;
586                             case "VAR_5":
587                                 citem.IM_VAR5 = string.IsNullOrEmpty(childIM.InnerText) ? " " : childIM.InnerText;
588                                 break;
589                             default:
590                                 //do nothing
591                                 break;
592                         }
593                         citem.ItemCost = Convert.ToDecimal(string.IsNullOrEmpty(cost) ? "0" : cost);
594                         citem.ItemPrice = Convert.ToDecimal(string.IsNullOrEmpty(price) ? "0" : price);
595                         citem.ItemSellPrice = Convert.ToDecimal(string.IsNullOrEmpty(sell) ? "0" : sell);
596                         citem.ItemWeight = Convert.ToDecimal(string.IsNullOrEmpty(weight) ? "0" : weight);
597                         citem.Sequence = recordSequence;
598                     }
599                     recordSequence += 1;
600                     DatabaseFactory.WriteRecordCItem(ref citem);
601                     cost = "";
602                     price = "";
603                     sell = "";
604                     weight = "";
605                     citem.IM_VAR1 = "";
606                     citem.IM_VAR2 = "";
607                     citem.IM_VAR3 = "";
608                     citem.IM_VAR4 = "";
609                     citem.IM_VAR5 = "";
610                 }
611 
612                 //BOM records (Must load in this manner, as none of these have static variable names or attributes)
613                 recordSequence = 0;
614                 XmlNodeList xnlb = detailDoc.GetElementsByTagName("Bom");
615                 bom.CO_Line = coitem.CO_Line;
616                 foreach (XmlNode nodeib in xnlb)
617                 {
618                     recordSequence += 1;
619                     bom.Sequence = recordSequence;
620                     bom.RecordSequence = recordSequence;
621                     var parent = nodeib.SelectSingleNode("..");
622                     string parentID = parent.ChildNodes[0].InnerText;
623                     bom.Parent = parentID == "1" ? null : parentID;
624                     bom.Identifier = nodeib.ChildNodes[0].InnerText;
625                     bom.Item = nodeib.ChildNodes[1].InnerText;
626                     bom.Smartpart = nodeib.ChildNodes[2].InnerText;
627                     bom.UnitPrice = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[3].InnerText) ? "0" : nodeib.ChildNodes[3].InnerText);
628                     bom.UnitCost = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[4].InnerText) ? "0" : nodeib.ChildNodes[4].InnerText);
629                     bom.Discount = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[5].InnerText) ? "0" : nodeib.ChildNodes[5].InnerText);
630                     bom.QTY = Convert.ToDecimal(string.IsNullOrEmpty(nodeib.ChildNodes[6].InnerText) ? "0" : nodeib.ChildNodes[6].InnerText);
631                     
632                     //Search the ItemMaster XML records for the matching smartpart_num and retrieve its priority level for this BOM record
633                     bool foundchild = false;
634                     bool imdone = false;
635                     foreach (XmlNode nodeIMPL in xnlim)
636                     {
637                         if (imdone == true)
638                         {
639                             break;
640                         }
641                         foreach (XmlNode childIMPL in nodeIMPL.ChildNodes)
642                         {
643                             if (imdone == true)
644                             {
645                                 break;
646                             }
647                             if (childIMPL.InnerText == bom.Smartpart)
648                             {
649                                 foundchild = true;
650                             }
651                             if (foundchild == true)
652                             {
653                                 if (childIMPL.Name == "PRIORITY_LEVEL")
654                                 {
655                                     bom.PriorityLevel = Convert.ToInt16(childIMPL.InnerText);
656                                     imdone = true;
657                                 }
658                             }
659                         }
660                     }
661                     //output BOM record
662                     DatabaseFactory.WriteRecordBOM(ref bom);
663                 }
664                 //Routing
665                 Triggers.logEvent = "STARTING ROUTING LOGIC";
666                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
667 
668                 int routebomSeq = 0;
669                 route.CO_Num = coitem.CO_Num;
670                 route.CO_Line = coitem.CO_Line;
671                 XmlNodeList xnlr = detailDoc.GetElementsByTagName("Routing");
672                 foreach (XmlNode nodecr in xnlr)
673                 {
674                     XmlNode nodecrtg = nodecr.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
675                     route.SmartpartNum = nodecrtg.ChildNodes[0].InnerText;
676                     nodecrtg = nodecr.SelectSingleNode("c1:ITEM_NUM", nsmgr);
677                     route.ItemNum = nodecrtg.ChildNodes[0].InnerText;
678                     nodecrtg = nodecr.SelectSingleNode("c1:BOM_ID", nsmgr);
679                     route.BOM_ID = nodecrtg.ChildNodes[0].InnerText;
680                                          
681                     //isolate OPERATION elements from Routing and iterate
682                     XmlNodeList xnlOperation = detailDoc.GetElementsByTagName("Operation");
683                     foreach(XmlNode nodeol in xnlOperation)
684                     {
685                         var operationParentTL = nodeol.SelectSingleNode("..");             //current operation becomes our new parent
686                         XmlDocument operationDocumentTL = new XmlDocument();
687                         operationDocumentTL.LoadXml(operationParentTL.OuterXml);
688                         XmlNodeList xnlOP = operationDocumentTL.GetElementsByTagName("OperationParam");
689                         foreach (XmlNode nodeParamChild in xnlOP)
690                         {
691                             if (nodeParamChild.ChildNodes[0].InnerText == "LABOR_HRS") { route.Labor_Hours = Convert.ToDouble(nodeParamChild.ChildNodes[2].InnerText); }
692                             if (nodeParamChild.ChildNodes[0].InnerText == "SETUP_HRS") { route.Setup_Hours = Convert.ToDouble(nodeParamChild.ChildNodes[2].InnerText); }
693                             if (nodeParamChild.ChildNodes[0].InnerText == "WC") { route.WC = nodeParamChild.ChildNodes[2].InnerText; }
694                             if (nodeParamChild.ChildNodes[0].InnerText == "NOTES") { route.Notes = nodeParamChild.ChildNodes[2].InnerText; }
695                             if (nodeParamChild.ChildNodes[0].InnerText == "MACH_NAME") { route.Machine_Name = nodeParamChild.ChildNodes[2].InnerText; }
696                         }
697 
698                         XmlNode nodeOpItem = nodeol.SelectSingleNode("c1:OPER_NUM", nsmgr);
699                         route.OPERATION = Convert.ToInt16(nodeOpItem.ChildNodes[0].InnerText);
700                         nodeOpItem = nodeol.SelectSingleNode("c1:DESCRIPTION", nsmgr);
701                         route.Description = nodeOpItem.ChildNodes[0].InnerText;
702 
703                         //set current operation as new parent and look ONLY for its OperationInput tags
704                         var operationParent = nodeol.SelectSingleNode(".");     //ensure we traverse ONLY children of this node (Operation) as the new parent 
705                         XmlDocument operationDoc = new XmlDocument();
706                         operationDoc.LoadXml(operationParent.OuterXml);
707                         XmlNodeList xnlOperationInputs = operationDoc.GetElementsByTagName("OperationInput");
708 
709                         foreach (XmlNode nodeOpInput in xnlOperationInputs)
710                         {
711                             XmlNode nodeoin = nodeOpInput.SelectSingleNode("c1:SMARTPART_NUM", nsmgr);
712                             route.MatlSmartpartNum = nodeOpInput.ChildNodes[0].InnerText;
713                             route.MatlItemNum = nodeOpInput.ChildNodes[1].InnerText;
714                             route.MatlQty = Convert.ToDecimal(nodeOpInput.ChildNodes[2].InnerText);
715                             //write to gr_cfgroutebom
716                             routebomSeq += 1;
717                             route.Seq = routebomSeq;
718                             DatabaseFactory.WriteRecordCfgRoute(ref route);
719                         }
720                     }
721                 }
722 
723                 Triggers.logEvent = "RESEQUENCING BOM RECORDS FOR ORDER: " + bom.CO_Num + " LINE: " + bom.CO_Line;
724                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
725                 DatabaseFactory.ResequenceBOM(bom.CO_Num, bom.CO_Line);
726             }
727             DatabaseFactory.WriteRecordCO(ref co);              //deferred write
728         }
729     }
730 }
