366         public static void CfgImport(string orderNumber)
367         {
368             //call SP to import data into Syteline
369             int CreateOrder = 1;
370             database = new DbConnection();
371             var command = database.Command;
372 
373             using (database.Connection)
374             {
375                 command = new SqlCommand("GR_CfgImportSp", database.Connection);
376                 command.CommandType = CommandType.StoredProcedure;
377                 command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
378                 command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
379                 command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
380                 command.CommandTimeout = 120000;
381 
382                 database.Connection.Open();
383                 try
384                 {
385                     command.ExecuteNonQuery();
386                 }
387                 catch (Exception spI)
388                 {
389                     //A timeout has likely occurred; try again
390                     string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
391                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
392 
393                     database.Connection.Close();
394                     database.Connection.Open();
395                     command.ExecuteNonQuery();
396                 }
397             }
398         }
