452         public static string RetrieveSLCO(string orderNum)
453         {
454             string SLOrderNumber = "";
455             database = new DbConnection();
456             var command = database.Command;
457 
458             using (database.Connection)
459             {
460                 try
461                 {
462                     SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with(nolock) Where c.order_num = @order_num";
463                     command = new SqlCommand(SQLCommand, database.Connection);
464                     command.Connection = database.Connection;
465                     command.CommandTimeout = 120000;
466                     SqlParameter order_num = command.Parameters.Add("@order_num", SqlDbType.NVarChar, 15);
467                     order_num.Value = orderNum;
468                     database.Connection.Open();
469 
470                     using (SqlDataReader reader = command.ExecuteReader())
471                     {
472                         if (reader.HasRows)
473                         {
474                             if (reader.Read())
475                             {
476                                 SLOrderNumber = reader["SLCO"].ToString();
477                             }
478                         }
479                         reader.Close();
480                     }
481                 }
482                 catch (Exception rCOex)
483                 {
484                     Triggers.logEvent = "ERROR RETRIEVING SLCO: " + rCOex.Message;
485                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
486                 }
487 
488                 database.Connection.Close();
489                 return SLOrderNumber;
490             }
491         }
