524         public static void InsertDocumentRecord(XmlDocument xml, string orderNum, string environment, string site, string SLorderNumber)
525         {
526             string maintConnectionString = connectionString;
527             connectionString = queueControlConnectionString;
528 
529             database = new DbConnection();
530             var command = database.Command;
531             database.Connection.Close();
532 
533             //ensure connection is closed before setting up to use it on a different CS
534             if (database.Connection.State == ConnectionState.Open)
535             {
536                 System.Threading.Thread.Sleep(1000);
537             }
538             
539             SQLCommand = "Insert Into GR_Cfg_DocumentQueue(order_num, environment, site, SLOrderNumber, orderXML) values(@orderNum, @environment, @site, @SLorderNumber, @xmldata)";
540             using (database.Connection)
541             {
542                 command = new SqlCommand(SQLCommand, database.Connection);
543                 command.Connection = database.Connection;
544                 command.Parameters.AddWithValue("@orderNum", orderNum);
545                 command.Parameters.AddWithValue("@environment", environment);
546                 command.Parameters.AddWithValue("@site", site);
547                 command.Parameters.AddWithValue("@SlorderNumber", SLorderNumber);
548                 command.Parameters.Add(new SqlParameter("@xmldata", System.Data.SqlDbType.Xml) { Value = new SqlXml(new XmlTextReader(xml.InnerXml, XmlNodeType.Document, null)) });
549                 command.CommandTimeout = 120000;
550                 database.Connection.Open();
551 
552                 //wait until connection is live before attempting to execute I/O
553                 if (database.Connection.State == ConnectionState.Connecting)
554                 {
555                     System.Threading.Thread.Sleep(1000);
556                 }
557 
558                 command.ExecuteNonQuery();
559                 database.Connection.Close();
560             }
561 
562             connectionString = maintConnectionString;
563             //to force the abstract class to reload our primary connnection string
564             database = new DbConnection();
565             command = database.Command;
566             command.Connection = database.Connection;
567             database.Connection.Open();
568             database.Connection.Close();
569         }
570         public static bool OrderCompleted(string order_num)
571         {
572             bool rowExists = false;
573 
574             database = new DbConnection();
575             var command = database.Command;
576 
577             SQLCommand = "Select order_num, co_num, stat from GR_CfgCO with(nolock) where order_num = @order_num and stat = 'C' and co_num is not null";
578             using (database.Connection)
579             {
580                 command = new SqlCommand(SQLCommand, database.Connection);
581                 command.Connection = database.Connection;
582                 command.CommandTimeout = 120000;
583                 SqlParameter ordernum = command.Parameters.AddWithValue("@order_num", order_num);
584                 database.Connection.Open();
585                 using (SqlDataReader reader = command.ExecuteReader())
586                 {
587                     if (reader.HasRows)
588                     {
589                         rowExists = true;
590                     }
591                     reader.Close();
592                 }
593                 database.Connection.Close();
594             }
595 
596             return rowExists;
597         }
598         public static int CoLines(string SLOrderNumber)
599         {
600             int coitems = 0;
601 
602             database = new DbConnection();
603             var command = database.Command;
604 
605             SQLCommand = "Select ISNULL(COUNT(co_line), 0) as lines From COITEM with(nolock) where co_num = @orderNumber";
606             using (database.Connection)
607             {
608                 command = new SqlCommand(SQLCommand, database.Connection);
609                 command.Connection = database.Connection;
610                 command.CommandTimeout = 120000;
611                 SqlParameter ordernum = command.Parameters.AddWithValue("@orderNumber", SLOrderNumber);
612                 database.Connection.Open();
613                 using (SqlDataReader reader = command.ExecuteReader())
614                 {
615                     reader.Read();
616                     coitems = Convert.ToInt16(reader["lines"].ToString());
617                     reader.Close();
618                 }
619                 database.Connection.Close();
620             }
621             return coitems;
622         }
