646         public static bool OrderExists(string C1orderNum)
647         {
648             bool orderExists = false;
649 
650             database = new DbConnection();
651             var command = database.Command;
652 
653             SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = @C1OrderNum Order By c.order_date desc, c.co_num desc";
654             using (database.Connection)
655             {
656                 command = new SqlCommand(SQLCommand, database.Connection);
657                 command.Connection = database.Connection;
658                 command.CommandTimeout = 120000;
659                 SqlParameter c1ordernum = command.Parameters.AddWithValue("@C1OrderNum", C1orderNum);
660                 database.Connection.Open();
661                 using (SqlDataReader reader = command.ExecuteReader())
662                 {
663                     if (reader.HasRows)
664                     {
665                         if (reader.Read())
666                         {
667                             orderExists = true;
668                         }
669                     }
670                     reader.Close();
671                 }
672                 database.Connection.Close();
673             }
674             return orderExists;
675         }
676         public static string CustomerOnHold(string custnum, string seq)
677         {
678             string holdReason = "";
679 
680             database = new DbConnection();
681             var command = database.Command;
682 
683             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = @custnum and cust_seq = @seq and uf_cohold = 1";
684             using (database.Connection)
685             {
686                 command = new SqlCommand(SQLCommand, database.Connection);
687                 command.Connection = database.Connection;
688                 command.CommandTimeout = 120000;
689                 SqlParameter CustNumber = command.Parameters.AddWithValue("@custnum", custnum);
690                 SqlParameter CustSeq = command.Parameters.AddWithValue("@seq", seq);
691                 database.Connection.Open();
692                 using (SqlDataReader reader = command.ExecuteReader())
693                 {
694                     if (reader.HasRows)
695                     {
696                         if (reader.Read())
697                         {
698                             holdReason = reader["uf_coholddescription"].ToString();
699                             Triggers.logEvent = "Found an on-hold record match";
700                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
701                         }
702                     }
703                     reader.Close();
704                 }
705                 database.Connection.Close();
706             }
707             return holdReason;
708         }
709         public static void MoveQueueRecord(string orderNum, string dbSite)
710         {
711             database = new DbConnection();
712             var command = database.Command;
713 
714             try
715             {
716                 //Disable trigger on target database/table, copy queue record, delete from NOVB
717                 SQLCommand = "DISABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
718                 using (database.Connection)
719                 {
720                     command = new SqlCommand(SQLCommand, database.Connection);
721                     command.Connection = database.Connection;
722                     command.CommandTimeout = 120000;
723                     database.Connection.Open();
724                     command.ExecuteNonQuery();
725                 }
726 
727                 string recordRowPointer = "";
728                 SQLCommand = "SELECT TOP 1 rowpointer from SL_NOVB_App.dbo.GR_Cfg_Queue where order_num = @orderNum order by recorddate desc";
729                 using (database.Connection)
730                 {
731                     command = new SqlCommand(SQLCommand, database.Connection);
732                     command.Connection = database.Connection;
733                     command.CommandTimeout = 120000;
734                     SqlParameter CustNumber = command.Parameters.AddWithValue("@orderNum", orderNum);
735                     database.Connection.Open();
736                     using (SqlDataReader reader = command.ExecuteReader())
737                     {
738                         if (reader.HasRows)
739                         {
740                             if (reader.Read())              //this should never NOT happen (ok that's a little obtuse.  How about: This should ALWAYS happen)
741                             {
742                                 //Copy this record to the target site DB
743                                 recordRowPointer = reader["rowpointer"].ToString();
744                                 reader.Close();
745 
746                                 SQLCommand = "insert into SL_" + dbSite + "_App.dbo.GR_Cfg_Queue Select * from SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = @recordrowpointer";
747                                 using (database.Connection)
748                                 {
749                                     command = new SqlCommand(SQLCommand, database.Connection);
750                                     command.Connection = database.Connection;
751                                     command.CommandTimeout = 120000;
752                                     SqlParameter RecRowPointer = command.Parameters.AddWithValue("@recordrowpointer", recordRowPointer);
753                                     database.Connection.Open();
754                                     command.ExecuteNonQuery();
755                                 }
756 
757                                 SQLCommand = "DELETE From SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = @recordrowpointer";
758                                 using (database.Connection)
759                                 {
760                                     command = new SqlCommand(SQLCommand, database.Connection);
761                                     command.Connection = database.Connection;
762                                     command.CommandTimeout = 120000;
763                                     SqlParameter RecRowPointer = command.Parameters.AddWithValue("@recordrowpointer", recordRowPointer);
764                                     database.Connection.Open();
765                                     command.ExecuteNonQuery();
766                                     database.Connection.Close();
767                                 }
768                             }
769                         }
770                     }
771                 }
772                 Triggers.logEvent = "Queue record was moved to SL_" + StagingUtilities.dbSite + "_App.dbo.GR_Cfg_Queue";
773                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
774             }
775             catch (Exception mqEx)
776             {
777                 //Log and ignore the error and move on with re-enabling the trigger
778                 string logEvent = "An error occurred while attempting to move the queue record to " + StagingUtilities.dbSite + ": " + mqEx.Message;
779                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
780             }
781 
782             //Re-enable trigger on db site queue table
783             SQLCommand = "ENABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
784             using (database.Connection)
785             {
786                 command = new SqlCommand(SQLCommand, database.Connection);
787                 command.Connection = database.Connection;
788                 command.CommandTimeout = 120000;
789                 database.Connection.Open();
790                 command.ExecuteNonQuery();
791             }
792         }
793     }
