38         public void SetConnectionString()
39         {
40             RegistryKey reg = Registry.CurrentUser.OpenSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0", true);
41             if (reg == null)
42             {
43                 //create keys if they don't already exist and set values
44                 reg = Registry.CurrentUser.CreateSubKey("SOFTWARE\\Microsoft\\ConfigureOneAssembly\\1.0");
45                 string dbConnectionData = File.ReadAllText(@"C:\C1\dbconnection.dat");
46                 string dbkey = File.ReadAllText(@"C:\C1\dbkey.dat");
47                 string proddbConnectionData = File.ReadAllText(@"C:\C1\proddbconnection.dat");
48                 emailaddr = File.ReadAllText(@"C:\C1\emailaddr.dat");
49                 emailServer = File.ReadAllText(@"C:\C1\emailserver.dat");
50                 emailFrom = File.ReadAllText(@"C:\C1\emailFromAddress.dat");
51                 splocation = File.ReadAllText(@"C:\C1\splocation.dat");
52                 spuname = File.ReadAllText(@"C:\C1\spuname.dat");
53                 sppassword = File.ReadAllText(@"C:\C1\sppassword.dat");
54                 dbprotect = File.ReadAllText(@"C:\C1\protect.dat");
55                 ws_uname = File.ReadAllText(@"C:\C1\ws_uname.dat");
56                 ws_password = File.ReadAllText(@"C:\C1\ws_password.dat");
57                 queueControlConnectionStringValueDEV = File.ReadAllText(@"C:\C1\queuecontroldev.dat");
58                 queueControlConnectionStringValuePROD = File.ReadAllText(@"C:\C1\queuecontrolprod.dat");
59                 //set registry values with defaults
60                 reg.SetValue("PROD_DB", proddbConnectionData);
61                 reg.SetValue("DB", dbConnectionData);
62                 reg.SetValue("ENCKEY", dbkey);
63                 reg.SetValue("EMAILADDR", emailaddr);
64                 reg.SetValue("EMAILSERVER", emailServer);
65                 reg.SetValue("EMAILFROM", emailFrom);
66                 reg.SetValue("SPLOCATION", splocation);
67                 reg.SetValue("SPUNAME", spuname);
68                 reg.SetValue("SPPASSWORD", sppassword);
69                 reg.SetValue("DEBUGLOGGING", "NO");
70                 reg.SetValue("PROTECT", dbprotect);
71                 reg.SetValue("WS_UNAME", ws_uname);
72                 reg.SetValue("WS_PASSWORD", ws_password);
73                 reg.SetValue("QueueControlConnectionDEV", queueControlConnectionStringValueDEV);
74                 reg.SetValue("QueueControlConnectionPROD", queueControlConnectionStringValuePROD);
75             }
76             
77             //decrypt cs
78             string sKey = reg.GetValue("ENCKEY").ToString();
79             connectionString = reg.GetValue("DB").ToString();
80             emailaddr = reg.GetValue("EMAILADDR").ToString();
81             emailServer = reg.GetValue("EMAILSERVER").ToString();
82             emailFrom = reg.GetValue("EMAILFROM").ToString();
83             splocation = reg.GetValue("SPLOCATION").ToString();
84             spuname = reg.GetValue("SPUNAME").ToString();
85             sppassword = reg.GetValue("SPPASSWORD").ToString();
86             connectionString = crypto.DecryptCS(connectionString, sKey);
87             spuname = crypto.DecryptCS(spuname, sKey);
88             sppassword = crypto.DecryptCS(sppassword, sKey);
89             dbprotect = reg.GetValue("PROTECT").ToString();
90             ws_uname = reg.GetValue("WS_UNAME").ToString();
91             ws_password = reg.GetValue("WS_PASSWORD").ToString();
92             queueControlConnectionStringValueDEV = reg.GetValue("QueueControlConnectionDEV").ToString();
93             queueControlConnectionStringValuePROD = reg.GetValue("QueueControlConnectionPROD").ToString();
94 
95             debugLogging = false;
96             if (reg.GetValue("DEBUGLOGGING").ToString() == "YES")
97             {
98                 debugLogging = true;
99             }
100 
101             queueControlConnectionString = queueControlConnectionStringValueDEV;
102             if (reg.GetValue("ENVIRONMENT").ToString() == "PROD") { queueControlConnectionString = queueControlConnectionStringValuePROD; }
103 
104             //if requested environment is not dev/test, change connectionString to prod; override to DEV if we sensed PROD but PROTECT in registry is YES
105             if (Triggers.dbEnvironment == "PROD" && reg.GetValue("PROTECT").ToString() != "YES")
106             {
107                 connectionString = reg.GetValue("PROD_DB").ToString();
108                 queueControlConnectionString = queueControlConnectionStringValuePROD;   //override ENVIRONMENT if incoming order is production
109             }
110             backupCS = connectionString;
111         }        
