347                     database.Command = new SqlCommand("GR_Cfg_ResequenceBOMSp", database.Connection);
348                     database.Command.CommandType = CommandType.StoredProcedure;
349                     database.Command.Parameters.AddWithValue("@OrderNumber", orderNumber);
350                     database.Command.Parameters.AddWithValue("@OrderLine", orderLine);
351                     database.Command.CommandTimeout = 120000;
