364         public static void CfgImport(string orderNumber)
365         {
366             //call SP to import data into Syteline
367             int CreateOrder = 1;
368             using (DbConnection database = new DbConnection())
369             {
370                 database.Command = new SqlCommand("GR_CfgImportSp", database.Connection);
371                 database.Command.CommandType = CommandType.StoredProcedure;
372                 database.Command.Parameters.AddWithValue("@pStartingOrderNum", orderNumber);
373                 database.Command.Parameters.AddWithValue("@pEndingOrderNum", orderNumber);
374                 database.Command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
375                 database.Command.CommandTimeout = 120000;
376                 database.Connection.Open();
377                 try
378                 {
379                     database.Command.ExecuteNonQuery();
380                 }
381                 catch (Exception spI)
382                 {
383                     //A timeout has likely occurred; try again
384                     string logEvent = "An error executing GR_CfgImportSp has occurred: " + spI.Message + " -> Retrying...";
385                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
386 
387                     database.Connection.Close();
388                     database.Connection.Open();
389                     database.Command.ExecuteNonQuery();
390                 }
391             }
392         }
393         public static void CleanupOrder(string orderNum)
394         {
395             using (DbConnection database = new DbConnection())
396             {
397                 database.Command = new SqlCommand("DELETE from GR_CfgCO where order_num = @ordernum", database.Connection);
398                 database.Command.Parameters.AddWithValue("@ordernum", orderNum);
399                 database.Command.CommandTimeout = 120000;
400                 database.Connection.Open();
401                 database.Command.ExecuteNonQuery();
402 
403                 database.Command = new SqlCommand("DELETE from GR_CfgCOItem where order_num = @ordernum", database.Connection);
404                 database.Command.Parameters.AddWithValue("@ordernum", orderNum);
405                 database.Command.ExecuteNonQuery();
406 
407                 database.Command = new SqlCommand("DELETE from GR_CfgBOM where order_num = @ordernum", database.Connection);
408                 database.Command.Parameters.AddWithValue("@ordernum", orderNum);
409                 database.Command.ExecuteNonQuery();
410 
411                 database.Command = new SqlCommand("DELETE from GR_CfgItem where order_num = @ordernum", database.Connection);
412                 database.Command.Parameters.AddWithValue("@ordernum", orderNum);
413                 database.Command.ExecuteNonQuery();
414 
415                 database.Command = new SqlCommand("DELETE from GR_CfgParmVal where order_num = @ordernum", database.Connection);
416                 database.Command.Parameters.AddWithValue("@ordernum", orderNum);
417                 database.Command.ExecuteNonQuery();
418 
419                 database.Command = new SqlCommand("DELETE from GR_CfgRouteBOM where order_num = @ordernum", database.Connection);
420                 database.Command.Parameters.AddWithValue("@ordernum", orderNum);
421                 database.Command.ExecuteNonQuery();
422                 database.Connection.Close();
423             }
424         }
