449         public static string RetrieveSLCO(string orderNum)
450         {
451             string SLOrderNumber = "";
452 
453             using (DbConnection database = new DbConnection())
454             {
455                 try
456                 {
457                     SQLCommand = "Select TOP 1 co_num as SLCO From GR_CfgCO c with(nolock) Where c.order_num = @order_num";
458                     database.Command = new SqlCommand(SQLCommand, database.Connection);
459                     database.Command.CommandTimeout = 120000;
460                     SqlParameter order_num = database.Command.Parameters.Add("@order_num", SqlDbType.NVarChar, 15);
461                     order_num.Value = orderNum;
462                     database.Connection.Open();
463 
464                     using (SqlDataReader reader = database.Command.ExecuteReader())
465                     {
466                         if (reader.HasRows)
467                         {
468                             if (reader.Read())
469                             {
470                                 SLOrderNumber = reader["SLCO"].ToString();
471                             }
472                         }
473                         reader.Close();
474                     }
475                 }
476                 catch (Exception rCOex)
477                 {
478                     Triggers.logEvent = "ERROR RETRIEVING SLCO: " + rCOex.Message;
479                     System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Error, 234);
480                 }
481 
482                 database.Connection.Close();
483                 return SLOrderNumber;
484             }
485         }
486         public static string RetrieveISOCountry(string ISOCode)
487         {
488             string ISOCountry = "";
489 
490             SQLCommand = "Select IsNULL(country, ' ') as country from country with (nolock) Where iso_country_code = @isoCode";
491             using (DbConnection database = new DbConnection())
492             {
493                 database.Command = new SqlCommand(SQLCommand, database.Connection);
494                 database.Command.CommandTimeout = 120000;
495                 SqlParameter iso_code = database.Command.Parameters.AddWithValue("@isoCode", ISOCode);
496                 database.Connection.Open();
497 
498                 using (SqlDataReader reader = database.Command.ExecuteReader())
499                 {
500                     if (reader.HasRows)
501                     {
502                         if (reader.Read())
503                         {
504                             ISOCountry = reader["country"].ToString();
505                         }
506                     }
507                     reader.Close();
508                 }
509                 database.Connection.Close();
510             }
511 
512             return ISOCountry;
513         }
514         public static void InsertDocumentRecord(XmlDocument xml, string orderNum, string environment, string site, string SLorderNumber)
515         {
516             string maintConnectionString = connectionString;
517             connectionString = queueControlConnectionString;
518             
519             SQLCommand = "Insert Into GR_Cfg_DocumentQueue(order_num, environment, site, SLOrderNumber, orderXML) values(@orderNum, @environment, @site, @SLorderNumber, @xmldata)";
520             using (DbConnection database = new DbConnection())
521             {
522                 database.Command = new SqlCommand(SQLCommand, database.Connection);
523                 database.Command.Parameters.AddWithValue("@orderNum", orderNum);
524                 database.Command.Parameters.AddWithValue("@environment", environment);
525                 database.Command.Parameters.AddWithValue("@site", site);
526                 database.Command.Parameters.AddWithValue("@SlorderNumber", SLorderNumber);
527                 database.Command.Parameters.Add(new SqlParameter("@xmldata", System.Data.SqlDbType.Xml) { Value = new SqlXml(new XmlTextReader(xml.InnerXml, XmlNodeType.Document, null)) });
528                 database.Command.CommandTimeout = 120000;
529                 database.Connection.Open();
530 
531                 database.Command.ExecuteNonQuery();
532                 database.Connection.Close();
533             }
534 
535             //to force the abstract class to reload our primary connnection string
536             connectionString = maintConnectionString;
537             using (DbConnection database = new DbConnection())
538             {
539                 database.Connection.Open();
540                 database.Connection.Close();
541             }
542         }
543         public static bool OrderCompleted(string order_num)
544         {
545             bool rowExists = false;
546 
547             SQLCommand = "Select order_num, co_num, stat from GR_CfgCO with(nolock) where order_num = @order_num and stat = 'C' and co_num is not null";
548             using (DbConnection database = new DbConnection())
549             {
550                 database.Command = new SqlCommand(SQLCommand, database.Connection);
551                 database.Command.CommandTimeout = 120000;
552                 SqlParameter ordernum = database.Command.Parameters.AddWithValue("@order_num", order_num);
553                 database.Connection.Open();
554                 using (SqlDataReader reader = database.Command.ExecuteReader())
555                 {
556                     if (reader.HasRows)
557                     {
558                         rowExists = true;
559                     }
560                     reader.Close();
561                 }
562                 database.Connection.Close();
563             }
564 
565             return rowExists;
566         }
567         public static int CoLines(string SLOrderNumber)
568         {
569             int coitems = 0;
570 
571             SQLCommand = "Select ISNULL(COUNT(co_line), 0) as lines From COITEM with(nolock) where co_num = @orderNumber";
572             using (DbConnection database = new DbConnection())
573             {
574                 database.Command = new SqlCommand(SQLCommand, database.Connection);
575                 database.Command.CommandTimeout = 120000;
576                 SqlParameter ordernum = database.Command.Parameters.AddWithValue("@orderNumber", SLOrderNumber);
577                 database.Connection.Open();
578                 using (SqlDataReader reader = database.Command.ExecuteReader())
579                 {
580                     reader.Read();
581                     coitems = Convert.ToInt16(reader["lines"].ToString());
582                     reader.Close();
583                 }
584                 database.Connection.Close();
585             }
586             return coitems;
587         }
