598                 database.Command.Parameters.AddWithValue("@pEndingOrderNum", "PRECACHE");
599                 database.Command.Parameters.AddWithValue("@pCreateOrder", CreateOrder);
600                 database.Command.CommandTimeout = 120000;
601 
