607         public static bool OrderExists(string C1orderNum)
608         {
609             bool orderExists = false;
610 
611             SQLCommand = "Select TOP 1 co_num as SLCO From CO c with (nolock) Where c.uf_weborder = @C1OrderNum Order By c.order_date desc, c.co_num desc";
612             using (DbConnection database = new DbConnection())
613             {
614                 database.Command = new SqlCommand(SQLCommand, database.Connection);
615                 database.Command.CommandTimeout = 120000;
616                 SqlParameter c1ordernum = database.Command.Parameters.AddWithValue("@C1OrderNum", C1orderNum);
617                 database.Connection.Open();
618                 using (SqlDataReader reader = database.Command.ExecuteReader())
619                 {
620                     if (reader.HasRows)
621                     {
622                         if (reader.Read())
623                         {
624                             orderExists = true;
625                         }
626                     }
627                     reader.Close();
628                 }
629                 database.Connection.Close();
630             }
631             return orderExists;
632         }
633         public static string CustomerOnHold(string custnum, string seq)
634         {
635             string holdReason = "";
636 
637             SQLCommand = "Select uf_coholddescription From customer cust with (nolock) Where ltrim(rtrim(cust.cust_num)) = @custnum and cust_seq = @seq and uf_cohold = 1";
638             using (DbConnection database = new DbConnection())
639             {
640                 database.Command = new SqlCommand(SQLCommand, database.Connection);
641                 database.Command.CommandTimeout = 120000;
642                 SqlParameter CustNumber = database.Command.Parameters.AddWithValue("@custnum", custnum);
643                 SqlParameter CustSeq = database.Command.Parameters.AddWithValue("@seq", seq);
644                 database.Connection.Open();
645                 using (SqlDataReader reader = database.Command.ExecuteReader())
646                 {
647                     if (reader.HasRows)
648                     {
649                         if (reader.Read())
650                         {
651                             holdReason = reader["uf_coholddescription"].ToString();
652                             Triggers.logEvent = "Found an on-hold record match";
653                             System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
654                         }
655                     }
656                     reader.Close();
657                 }
658                 database.Connection.Close();
659             }
660             return holdReason;
661         }
662         public static void MoveQueueRecord(string orderNum, string dbSite)
663         {
664             try
665             {
666                 //Disable trigger on target database/table, copy queue record, delete from NOVB
667                 SQLCommand = "DISABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
668                 using (DbConnection database = new DbConnection())
669                 {
670                     database.Command = new SqlCommand(SQLCommand, database.Connection);
671                     database.Command.CommandTimeout = 120000;
672                     database.Connection.Open();
673                     database.Command.ExecuteNonQuery();
674                     database.Connection.Close();
675                 }
676 
677                 string recordRowPointer = "";
678                 SQLCommand = "SELECT TOP 1 rowpointer from SL_NOVB_App.dbo.GR_Cfg_Queue where order_num = @orderNum order by recorddate desc";
679                 using (DbConnection database = new DbConnection())
680                 {
681                     database.Command = new SqlCommand(SQLCommand, database.Connection);
682                     database.Command.CommandTimeout = 120000;
683                     SqlParameter CustNumber = database.Command.Parameters.AddWithValue("@orderNum", orderNum);
684                     database.Connection.Open();
685                     using (SqlDataReader reader = database.Command.ExecuteReader())
686                     {
687                         if (reader.HasRows)
688                         {
689                             if (reader.Read())              //this should never NOT happen (ok that's a little obtuse.  How about: This should ALWAYS happen)
690                             {
691                                 //Copy this record to the target site DB
692                                 recordRowPointer = reader["rowpointer"].ToString();
693                                 reader.Close();
694 
695                                 SQLCommand = "insert into SL_" + dbSite + "_App.dbo.GR_Cfg_Queue Select * from SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = @recordrowpointer";
696                                 using (database.Connection)
697                                 {
698                                     database.Command = new SqlCommand(SQLCommand, database.Connection);
699                                     database.Command.CommandTimeout = 120000;
700                                     SqlParameter RecRowPointer = database.Command.Parameters.AddWithValue("@recordrowpointer", recordRowPointer);
701                                     database.Command.ExecuteNonQuery();
702 
703                                     SQLCommand = "DELETE From SL_NOVB_App.dbo.GR_Cfg_Queue where rowpointer = @recordrowpointer";
704                                     database.Command = new SqlCommand(SQLCommand, database.Connection);
705                                     database.Command.CommandTimeout = 120000;
706                                     RecRowPointer = database.Command.Parameters.AddWithValue("@recordrowpointer", recordRowPointer);
707                                     database.Command.ExecuteNonQuery();
708                                     database.Connection.Close();
709                                 }
710                             }
711                         }
712                     }
713                 }
714                 Triggers.logEvent = "Queue record was moved to SL_" + StagingUtilities.dbSite + "_App.dbo.GR_Cfg_Queue";
715                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, Triggers.logEvent, System.Diagnostics.EventLogEntryType.Information, 234);
716             }
717             catch (Exception mqEx)
718             {
719                 //Log and ignore the error and move on with re-enabling the trigger
720                 string logEvent = "An error occurred while attempting to move the queue record to " + StagingUtilities.dbSite + ": " + mqEx.Message;
721                 System.Diagnostics.EventLog.WriteEntry(Triggers.logSource, logEvent, System.Diagnostics.EventLogEntryType.Warning, 234);
722             }
723 
724             //Re-enable trigger on db site queue table
725             SQLCommand = "ENABLE TRIGGER C1Order ON SL_" + dbSite + "_App.dbo.GR_Cfg_Queue";
726             using (DbConnection database = new DbConnection())
727             {
728                 database.Command = new SqlCommand(SQLCommand, database.Connection);
729                 database.Command.CommandTimeout = 120000;
730                 database.Connection.Open();
731                 database.Command.ExecuteNonQuery();
732                 database.Connection.Close();
733             }
734         }
735     }
736 }
